export interface LoginRequest {
  email: string;
  password: string;
}

export interface LoginResponse {
  first_name: string;
  last_name: string;
  clinic_name: string;
  doctor_id: string;
}

export interface AuthState {
  isAuthenticated: boolean;
  user: LoginResponse | null;
  token: string | null;
}

export interface LoginRequest {
  email: string;
  password: string;
}

export interface LoginResponse {
  id: string;
  first_name: string;
  last_name: string;
  clinic_name: string;
}

export interface AuthState {
  isAuthenticated: boolean;
  user: LoginResponse | null;
  token: string | null;
}

import { Appointment } from './Appointment';

// Base Patient interface matching simplified API schema
export interface Patient {
    id: string;
    first_name: string;
    last_name: string;
    date_of_birth: string; // ISO date string
    gender: 'male' | 'female';
    email: string;
    phone: string;
    street: string;
    city: string;
    state: string;
    zip_code: string;
}

// For creating new patients (without id)
export interface PatientCreate {
    first_name: string;
    last_name: string;
    phone: string;
    date_of_birth?: string;
    gender?: 'male' | 'female';
    email?: string;
    street?: string;
    city?: string;
    state?: string;
    zip_code?: string;
}

// For updating patients (all fields optional)
export interface PatientUpdate {
    first_name?: string;
    last_name?: string;
    date_of_birth?: string;
    gender?: 'male' | 'female';
    email?: string;
    phone?: string;
    street?: string;
    city?: string;
    state?: string;
    zip_code?: string;
}

// For API responses (matches PatientRead from OpenAPI)
export interface PatientRead extends Patient {}

// Patient with appointments (matches PatientReadWithAppointments from OpenAPI)
export interface PatientWithAppointments extends Patient {
    appointments?: Appointment[] | null;
}

// Paginated response for patient list
export interface PatientListResponse {
    data: Patient[];
    total: number;
    limit: number;
    offset: number;
}

// Patient summary statistics
export interface PatientSummary {
    total_patients: number;
    new_patients: number;
    patients_with_follow_up: number;
    patients_with_email: number;
}

// filepath: /home/imari/IdeaProjects/imari_fe2/src/types/Settings.ts

export interface Settings {
  // Localization
  language: string;
  country: string;
  city: string;
  timezone: string;

  // Clinic Information
  clinicName: string;
  clinicAddress: string;
  clinicPhone: string;
  clinicEmail: string;

  // Appointment Settings
  appointmentDuration: number; // in minutes
  workingHoursStart: string; // HH:MM format
  workingHoursEnd: string; // HH:MM format
  workingDays: string[]; // ['monday', 'tuesday', etc.]

  // Notification Settings
  emailNotifications: boolean;
  smsNotifications: boolean;
  appointmentReminders: boolean;
  reminderTimeBefore: number; // in minutes

  // Display Settings
  dateFormat: string; // 'DD/MM/YYYY' | 'MM/DD/YYYY' | 'YYYY-MM-DD'
  timeFormat: '12h' | '24h';
  currency: string;

  // Weather Settings
  showWeather: boolean;
  temperatureUnit: 'celsius' | 'fahrenheit';
}

export interface SettingsUpdate {
  [key: string]: any;
}

export interface Country {
  code: string;
  name: string;
  cities: string[];
}

export interface Language {
  code: string;
  name: string;
  nativeName: string;
}

// filepath: /home/imari/IdeaProjects/imari_fe2/src/types/Settings.ts

export interface Settings {
  // Clinic Information
  clinic_name: string;
  clinic_address: string;
  clinic_phone: string;
  clinic_email: string;

  // Appointment Settings
  appointments_start_time: string; // HH:MM format
  appointments_end_time: string; // HH:MM format
  appointments_working_days: string[]; // ['Monday', 'Tuesday', etc.]

  // Notification Settings
  notifications_email: boolean;
  notifications_sms: boolean;
  notifications_appointment_reminder: boolean;
  notifications_reminder_time: number; // in minutes

  // Display Settings
  display_date_format: string; // 'DD/MM/YYYY' | 'MM/DD/YYYY' | 'YYYY-MM-DD'
  display_time_format: string; // '12h' | '24h'
  display_currency: string;
  display_temperature_unit: string; // 'celsius' | 'fahrenheit'
  display_language: string;

  // IDs
  id: string;
  doctor_id: string;
}

export interface SettingsUpdate {
  [key: string]: any;
}

export interface Country {
  code: string;
  name: string;
  cities: string[];
}

export interface Language {
  code: string;
  name: string;
  nativeName: string;
}

export interface SettingsFieldValues {
  weekDays: string[];
  dateFormats: string[];
  timeFormats: string[];
  currencies: string[];
  temperatureUnits: string[];
  languages: string[];
}
