  currentDate,
  appointments,
  onTimeSlotClick,
  onAppointmentClick,
  onAppointmentDrop,
  getAppointmentDate,
  formatAppointmentTime,
  getAppointmentDuration,
  getAppointmentBackgroundColor,
  getAppointmentTextColor,
  getPatientName,
  handleMouseUp,
  handleMouseDown,
  handleMouseEnter,
  isSlotSelected,
}) => {
