          <p className="text-sm text-neutral-500">{currentDate.toLocaleDateString('en-US', { weekday: 'short' })}</p>
          <p className={`text-lg font-semibold ${
            currentDate.toDateString() === new Date().toDateString()
              ? 'text-primary-600'
              : 'text-neutral-800'
          }`}>
            {currentDate.getDate()}
