import { AppointmentService } from '../services/appointmentService';
import { PatientService } from '../services/patientService';
import { Appointment, AppointmentUpdate, AppointmentStatus, AppointmentCreate } from '../types/Appointment';
import { Patient, PatientCreate, PatientUpdate } from '../types/Patient';
import { useNotification } from '../context/NotificationContext';
import '../styles/calendar-day.css'; // Import the calendar day CSS
import { RescheduleConfirmation } from '../components/calendar/RescheduleConfirmation';

type CalendarView = 'month' | 'week' | 'day';

export const Calendar: React.FC = () => {
  const { t } = useTranslation();
  const [currentDate, setCurrentDate] = useState(new Date());
  const [view, setView] = useState<CalendarView>('week');
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedAppointment, setSelectedAppointment] = useState<Appointment | null>(null);
  const [selectedPatient, setSelectedPatient] = useState<Patient | null>(null);
  const [appointmentLoading, setAppointmentLoading] = useState(false);
  const [showNewAppointmentForm, setShowNewAppointmentForm] = useState(false);
  const [selectedPatientForBooking, setSelectedPatientForBooking] = useState<Patient | null>(null);
  const [patients, setPatients] = useState<Patient[]>([]);
  const [bookingStep, setBookingStep] = useState<'patient' | 'appointment'>('patient');
  const [selectedTimeSlot, setSelectedTimeSlot] = useState<{date: string, time: string, endTime?: string} | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchingPatients, setIsSearchingPatients] = useState(false);
  const [showCreatePatientForm, setShowCreatePatientForm] = useState(false);

  // Reschedule confirmation state
  const [showRescheduleConfirmation, setShowRescheduleConfirmation] = useState(false);
  const [rescheduleData, setRescheduleData] = useState<{
    appointment: Appointment;
    patient: Patient;
    newDate: string;
    newStartTime: string;
    newEndTime: string;
  } | null>(null);

  // Drag and drop state
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState<{date: string, time: string} | null>(null);
  const [dragEnd, setDragEnd] = useState<{date: string, time: string} | null>(null);

  const { showNotification } = useNotification();

  // Load appointments from API
  const loadAppointments = async () => {
    setLoading(true);
    setError(null);
    try {
      // Calculate date range based on current view
      const startDate = getViewStartDate();
      const days = getViewDays();

      const appointmentData = await AppointmentService.listAppointmentsByRange(
        startDate.toISOString().split('T')[0],
        days
      );
      setAppointments(appointmentData);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load appointments');
      console.error('Error loading appointments:', err);
    } finally {
      setLoading(false);
    }
  };

  // Load patients for appointment booking
  const loadPatients = async () => {
    try {
      const response = await PatientService.listPatients();
      setPatients(response.data);
    } catch (err) {
      console.error('Error loading patients:', err);
    }
  };

  // Load data on component mount and when date/view changes
  useEffect(() => {
    loadAppointments();
    loadPatients();
  }, [currentDate, view]);

  // Handle appointment creation
  const handleCreateAppointment = async (appointmentData: AppointmentCreate) => {
    setAppointmentLoading(true);
    try {
      const newAppointment = await AppointmentService.createAppointment(appointmentData);
      setAppointments(prev => [...prev, newAppointment]);
      setShowNewAppointmentForm(false);
      setSelectedTimeSlot(null);
      showNotification('success', 'Success', 'Appointment created successfully');
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create appointment');
      console.error('Error creating appointment:', err);
    } finally {
      setAppointmentLoading(false);
    }
  };

  // Handle appointment update
  const handleUpdateAppointment = async (appointmentId: string, appointmentData: AppointmentUpdate) => {
    setAppointmentLoading(true);
    try {
      const updatedAppointment = await AppointmentService.updateAppointment(appointmentId, appointmentData);
      setAppointments(prev => prev.map(apt => apt.id === appointmentId ? updatedAppointment : apt));
      setSelectedAppointment(updatedAppointment);
      showNotification('success', 'Success', 'Appointment updated successfully');
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update appointment');
      console.error('Error updating appointment:', err);
    } finally {
      setAppointmentLoading(false);
    }
  };

  // Handle appointment deletion
  const handleDeleteAppointment = async (appointmentId: string) => {
    if (!window.confirm('Are you sure you want to delete this appointment?')) {
      return;
    }

    setAppointmentLoading(true);
    try {
      await AppointmentService.deleteAppointment(appointmentId);
      setAppointments(prev => prev.filter(apt => apt.id !== appointmentId));
      setSelectedAppointment(null);
      showNotification('success', 'Success', 'Appointment deleted successfully');
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to delete appointment');
      console.error('Error deleting appointment:', err);
    } finally {
      setAppointmentLoading(false);
    }
  };

  // Handle appointment status update
  const handleUpdateAppointmentStatus = async (appointmentId: string, status: AppointmentStatus) => {
    setAppointmentLoading(true);
    try {
      const updatedAppointment = await AppointmentService.updateAppointment(appointmentId, { status });
      setAppointments(prev => prev.map(apt => apt.id === appointmentId ? updatedAppointment : apt));
      setSelectedAppointment(updatedAppointment);
      showNotification('success', 'Success', `Appointment ${status.toLowerCase()}`);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update appointment status');
      console.error('Error updating appointment status:', err);
    } finally {
      setAppointmentLoading(false);
    }
  };

  // Handle appointment cancellation
  const handleCancelAppointment = async (appointmentId: string) => {
    if (!window.confirm('Are you sure you want to cancel this appointment?')) {
      return;
    }

    setAppointmentLoading(true);
    try {
      const updatedAppointment = await AppointmentService.updateAppointment(appointmentId, { status: 'Cancelled' });
      setAppointments(prev => prev.map(apt => apt.id === appointmentId ? updatedAppointment : apt));
      setSelectedAppointment(updatedAppointment);
      showNotification('success', 'Success', 'Appointment cancelled');
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to cancel appointment');
      console.error('Error cancelling appointment:', err);
    } finally {
      setAppointmentLoading(false);
    }
  };

  // Handle new appointment flow
  const handleNewAppointmentClick = () => {
    setShowNewAppointmentForm(true);
    setBookingStep('patient');
    setSelectedPatientForBooking(null);
    setSearchQuery('');
    setShowCreatePatientForm(false);
  };

  // Handle patient selection for booking
  const handlePatientSelect = (patient: Patient) => {
    // Set the selected patient and transition to appointment booking step
    setSelectedPatientForBooking(patient);
    setBookingStep('appointment');
  };

  // Reset booking flow when closing the modal
  const closeNewAppointmentModal = () => {
    setShowNewAppointmentForm(false);

    // Reset the booking flow after the animation completes
    setTimeout(() => {
      setBookingStep('patient');
      setSelectedPatientForBooking(null);
      setSelectedTimeSlot(null);
      setShowCreatePatientForm(false);
      setSearchQuery('');
    }, 300);
  };

  // Handle patient creation
  const handleCreatePatient = async (patientData: PatientCreate | PatientUpdate): Promise<void> => {
    try {
      // Create clean data object with only non-empty fields
      const createData: any = {
        first_name: patientData.first_name || '',
        last_name: patientData.last_name || '',
        phone: patientData.phone || '',
        gender: patientData.gender || 'male',
      };

      // Only add optional fields if they have values
      if (patientData.email && patientData.email.trim()) {
        createData.email = patientData.email.trim();
      }
      if (patientData.date_of_birth && patientData.date_of_birth.trim()) {
        createData.date_of_birth = patientData.date_of_birth.trim();
      }
      if (patientData.street && patientData.street.trim()) {
        createData.street = patientData.street.trim();
      }
      if (patientData.city && patientData.city.trim()) {
        createData.city = patientData.city.trim();
      }
      if (patientData.state && patientData.state.trim()) {
        createData.state = patientData.state.trim();
      }
      if (patientData.zip_code && patientData.zip_code.trim()) {
        createData.zip_code = patientData.zip_code.trim();
      }

      const newPatient = await PatientService.createPatient(createData);
      setPatients(prev => [...prev, newPatient]);
      setSelectedPatientForBooking(newPatient);
      setShowCreatePatientForm(false);
      setBookingStep('appointment');
      showNotification('success', 'Success', 'Patient created successfully');
    } catch (err) {
      console.error('Error creating patient:', err);
      showNotification('error', 'Error', 'Failed to create patient');
      // Re-throw the error so PatientForm can handle it appropriately
      throw err;
    }
  };

  // Handle appointment booking completion
  const handleAppointmentBookingComplete = async (appointmentData: AppointmentCreate) => {
    await handleCreateAppointment(appointmentData);
    // Reset booking state
    setBookingStep('patient');
    setSelectedPatientForBooking(null);
    setSearchQuery('');
    setShowCreatePatientForm(false);
  };

  // Search patients using API
  const searchPatients = useCallback(async (query: string) => {
    if (!query.trim()) {
      loadPatients();
      return;
    }

    setIsSearchingPatients(true);
    try {
      const response = await PatientService.searchPatients(query.trim(), 0, 100);
      setPatients(response.data);
    } catch (err) {
      console.error('Error searching patients:', err);
      showNotification('error', 'Error', 'Failed to search patients');
    } finally {
      setIsSearchingPatients(false);
    }
  }, []);

  // Debounced search effect for patients
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (searchQuery.trim()) {
        searchPatients(searchQuery);
      } else {
        loadPatients();
      }
    }, 300); // 300ms debounce

    return () => clearTimeout(timeoutId);
  }, [searchQuery, searchPatients]);

  // Helper functions for date calculations
  const getViewStartDate = () => {
    const date = new Date(currentDate);
    if (view === 'week') {
      date.setDate(date.getDate() - date.getDay()); // Start of week
    } else if (view === 'month') {
      date.setDate(1); // Start of month
    }
    return date;
  };

  const getViewDays = () => {
    switch (view) {
      case 'day': return 1;
      case 'week': return 7;
      case 'month': return 31; // Approximate
      default: return 7;
    }
  };

  const getStatusColor = (status: AppointmentStatus) => {
    switch (status) {
      case 'Completed': return 'bg-green-100 text-green-800';
      case 'Booked': return 'bg-blue-100 text-blue-800';
      case 'Cancelled': return 'bg-red-100 text-red-800';
      case 'No Show': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  // Get background color for appointment status
  const getAppointmentBackgroundColor = (status: AppointmentStatus) => {
    switch (status) {
      case 'Completed': return '#dcfce7'; // Light green
      case 'Booked': return '#dbeafe';    // Light blue
      case 'Cancelled': return '#fee2e2';  // Light red
      case 'No Show': return '#ffedd5';    // Light orange
      default: return '#f3f4f6';          // Light gray
    }
  };

  // Get text color for appointment status
  const getAppointmentTextColor = (status: AppointmentStatus) => {
    switch (status) {
      case 'Completed': return '#166534'; // Dark green
      case 'Booked': return '#1e40af';    // Dark blue
      case 'Cancelled': return '#b91c1c';  // Dark red
      case 'No Show': return '#c2410c';    // Dark orange
      default: return '#4b5563';          // Dark gray
    }
  };

  const formatDate = (date: Date) => {
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const navigateDate = (direction: 'prev' | 'next') => {
    const newDate = new Date(currentDate);
    if (view === 'day') {
      newDate.setDate(newDate.getDate() + (direction === 'next' ? 1 : -1));
    } else if (view === 'week') {
      newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
    } else {
      newDate.setMonth(newDate.getMonth() + (direction === 'next' ? 1 : -1));
    }
    setCurrentDate(newDate);
  };

  // Format appointment time from API response
  const formatAppointmentTime = (appointment: Appointment) => {
    if (appointment.start_time) {
      // Convert time format (e.g., "09:00:00.000000") to "09:00"
      return appointment.start_time.split(':').slice(0, 2).join(':');
    }
    return '00:00';
  };

  // Get appointment date from API response
  const getAppointmentDate = (appointment: Appointment) => {
    if (appointment.date) {
      return appointment.date.split('T')[0]; // Extract date part from ISO string
    }
    return new Date().toISOString().split('T')[0];
  };

  // Get patient name from appointment
  const getPatientName = (appointment: Appointment) => {
    return `${appointment.patient_first_name} ${appointment.patient_last_name}`;
  };

  // Calculate appointment duration in minutes
  const getAppointmentDuration = (appointment: Appointment) => {
    if (appointment.start_time && appointment.end_time) {
      const start = new Date(`2000-01-01T${appointment.start_time}`);
      const end = new Date(`2000-01-01T${appointment.end_time}`);
      return Math.round((end.getTime() - start.getTime()) / (1000 * 60)); // Duration in minutes
    }
    return 30; // Default duration
  };

  // Load patient data for the selected appointment
  const loadPatientForAppointment = useCallback(async (appointment: Appointment) => {
    if (!appointment || !appointment.patient_id) return;

    try {
      setAppointmentLoading(true);
      const patientData = await PatientService.getPatient(appointment.patient_id);
      setSelectedPatient(patientData);
    } catch (err) {
      console.error('Error loading patient data:', err);
      setError(err instanceof Error ? err.message : 'Failed to load patient data');
      showNotification('error', 'Error', 'Failed to load patient data');
    } finally {
      setAppointmentLoading(false);
    }
  }, [showNotification]);

  // Handle appointment selection
  useEffect(() => {
    if (selectedAppointment) {
      loadPatientForAppointment(selectedAppointment);
    } else {
      setSelectedPatient(null);
    }
  }, [selectedAppointment, loadPatientForAppointment]);

  // Format appointments data for MonthView component
  const groupAppointmentsByDate = () => {
    const grouped: Record<string, any[]> = {};

    appointments.forEach(appointment => {
      const dateStr = getAppointmentDate(appointment);

      if (!grouped[dateStr]) {
        grouped[dateStr] = [];
      }

      grouped[dateStr].push({
        id: appointment.id,
        patientName: getPatientName(appointment),
        time: formatAppointmentTime(appointment),
        duration: getAppointmentDuration(appointment),
        type: appointment.type,
        status: appointment.status,
        title: appointment.type || 'Appointment'
      });
    });

    return grouped;
  };

  const renderCalendarHeader = () => (
    <div className="card mb-8">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-3xl font-bold text-primary-600 mb-2">Calendar</h1>
          <p className="text-neutral-600">{formatDate(currentDate)}</p>
        </div>

        {/* View Toggle */}
        <div className="flex items-center space-x-2">
          <div className="bg-neutral-100 rounded-xl p-1 flex border border-neutral-200">
            {(['month', 'week', 'day'] as CalendarView[]).map((viewType) => (
              <button
                key={viewType}
                onClick={() => setView(viewType)}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-all duration-300 ${
                  view === viewType
                    ? 'bg-primary-500 text-white shadow-primary'
                    : 'text-neutral-600 hover:text-primary-600 hover:bg-white'
                }`}
              >
                {viewType.charAt(0).toUpperCase() + viewType.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Navigation and Actions */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <button
            onClick={() => navigateDate('prev')}
            className="btn-secondary p-3"
          >
            <span className="material-icons-round">chevron_left</span>
          </button>
          <button
            onClick={() => setCurrentDate(new Date())}
            className="btn-secondary"
          >
            Today
          </button>
          <button
            onClick={() => navigateDate('next')}
            className="btn-secondary p-3"
          >
            <span className="material-icons-round">chevron_right</span>
          </button>
        </div>

        <div className="flex items-center space-x-3">
          <button
            onClick={handleNewAppointmentClick}
            className="btn-primary"
          >
            <span className="material-icons-round mr-2">add</span>
            New Appointment
          </button>
        </div>
      </div>
    </div>
  );

  // Drag and drop handlers
  const handleMouseDown = (e: React.MouseEvent, date: string, time: string) => {
    e.preventDefault();
    setIsDragging(true);
    setDragStart({ date, time });
    setDragEnd({ date, time });
    console.log('Starting drag at:', date, time);
  };

  const handleMouseEnter = (date: string, time: string) => {
    if (isDragging && dragStart && dragStart.date === date) {
      setDragEnd({ date, time });
    }
  };

  const handleMouseUp = () => {
    if (isDragging && dragStart && dragEnd) {
      const timeSlots = Array.from({ length: 40 }, (_, i) => {
        const hour = Math.floor(i / 4) + 8;
        const minutes = (i % 4) * 15;
        return `${hour.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
      });

      const startIndex = timeSlots.indexOf(dragStart.time);
      const endIndex = timeSlots.indexOf(dragEnd.time);

      if (startIndex !== -1 && endIndex !== -1) {
        const minIndex = Math.min(startIndex, endIndex);
        const maxIndex = Math.max(startIndex, endIndex);
        const startTime = timeSlots[minIndex];
        const endTime = timeSlots[maxIndex + 1] || timeSlots[maxIndex]; // End time is next slot or same if last

        console.log('Drag completed:', {
          date: dragStart.date,
          startTime,
          endTime
        });

        setSelectedTimeSlot({
          date: dragStart.date,
          time: startTime,
          endTime
        });

        handleNewAppointmentClick();
      }
    }

    setIsDragging(false);
    setDragStart(null);
    setDragEnd(null);
  };

  // Check if a slot is selected in the drag
  const isSlotSelected = (date: string, time: string) => {
    if (!isDragging || !dragStart || !dragEnd || dragStart.date !== date) {
      return false;
    }

    const timeSlots = Array.from({ length: 40 }, (_, i) => {
      const hour = Math.floor(i / 4) + 8;
      const minutes = (i % 4) * 15;
      return `${hour.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
    });

    const currentIndex = timeSlots.indexOf(time);
    const startIndex = timeSlots.indexOf(dragStart.time);
    const endIndex = timeSlots.indexOf(dragEnd.time);

    if (currentIndex === -1 || startIndex === -1 || endIndex === -1) {
      return false;
    }

    const minIndex = Math.min(startIndex, endIndex);
    const maxIndex = Math.max(startIndex, endIndex);

    return currentIndex >= minIndex && currentIndex <= maxIndex;
  };

  // Handle appointment rescheduling via drag and drop
  const handleAppointmentDrop = async (appointment: Appointment, newDate: string, newTime: string) => {
    // Calculate new end time based on appointment duration
    const duration = getAppointmentDuration(appointment);
    const [hours, minutes] = newTime.split(':').map(Number);
    const newStartTime = `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:00`;

    // Calculate end time
    const totalMinutes = hours * 60 + minutes + duration;
    const endHours = Math.floor(totalMinutes / 60);
    const endMinutes = totalMinutes % 60;
    const newEndTime = `${endHours.toString().padStart(2, '0')}:${endMinutes.toString().padStart(2, '0')}:00`;

    // Load patient data and show reschedule confirmation
    try {
      setAppointmentLoading(true);
      const patientData = await PatientService.getPatient(appointment.patient_id);

      setRescheduleData({
        appointment,
        patient: patientData,
        newDate,
        newStartTime,
        newEndTime
      });
      setShowRescheduleConfirmation(true);
    } catch (err) {
      console.error('Error loading patient data:', err);
      showNotification('error', 'Error', 'Failed to load patient data');
    } finally {
      setAppointmentLoading(false);
    }
  };

  // Handle reschedule confirmation
  const handleRescheduleConfirm = async (appointmentData: AppointmentUpdate) => {
    if (!rescheduleData) return;

    try {
      setAppointmentLoading(true);
      const updatedAppointment = await AppointmentService.updateAppointment(rescheduleData.appointment.id, appointmentData);
      setAppointments(prev => prev.map(apt => apt.id === rescheduleData.appointment.id ? updatedAppointment : apt));
      showNotification('success', 'Success', 'Appointment rescheduled successfully');
      setShowRescheduleConfirmation(false);
      setRescheduleData(null);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to reschedule appointment');
      showNotification('error', 'Error', 'Failed to reschedule appointment');
      console.error('Error rescheduling appointment:', err);
    } finally {
      setAppointmentLoading(false);
    }
  };

  // Handle reschedule cancel
  const handleRescheduleCancel = () => {
    setShowRescheduleConfirmation(false);
    setRescheduleData(null);
  };

  return (
    <DashboardLayout>
      <div className="fade-in-element">
        {renderCalendarHeader()}

        {view === 'week' && (
          <WeeklyView
            currentDate={currentDate}
            appointments={appointments}
            handleMouseUp={handleMouseUp}
            handleMouseDown={handleMouseDown}
            handleMouseEnter={handleMouseEnter}
            onSelectSlot={(date, time) => { setSelectedTimeSlot({ date, time }); handleNewAppointmentClick(); }}
            onAppointmentClick={setSelectedAppointment}
            onAppointmentDrop={handleAppointmentDrop}
            isSlotSelected={isSlotSelected}
            getAppointmentDate={getAppointmentDate}
            formatAppointmentTime={formatAppointmentTime}
            getAppointmentDuration={getAppointmentDuration}
            getAppointmentBackgroundColor={getAppointmentBackgroundColor}
            getAppointmentTextColor={getAppointmentTextColor}
          />
        )}
        {view === 'day' && (
          <DayView
            currentDate={currentDate}
            appointments={appointments}
            onTimeSlotClick={(date, time) => { setSelectedTimeSlot({ date, time }); handleNewAppointmentClick(); }}
            onAppointmentClick={setSelectedAppointment}
            onAppointmentDrop={handleAppointmentDrop}
            getAppointmentDate={getAppointmentDate}
            formatAppointmentTime={formatAppointmentTime}
            getAppointmentDuration={getAppointmentDuration}
            getAppointmentBackgroundColor={getAppointmentBackgroundColor}
            getAppointmentTextColor={getAppointmentTextColor}
            getPatientName={getPatientName}
            handleMouseUp={handleMouseUp}
            handleMouseDown={handleMouseDown}
            handleMouseEnter={handleMouseEnter}
            isSlotSelected={isSlotSelected}
          />
        )}
        {view === 'month' && (
          <div className="card">
            <MonthView
              currentDate={currentDate}
              appointments={groupAppointmentsByDate()}
              onAppointmentClick={(appointmentId) => {
                const appointment = appointments.find(apt => apt.id === appointmentId);
                if (appointment) {
                  setSelectedAppointment(appointment);
                }
              }}
              onDayClick={(date) => {
                setCurrentDate(date);
                setView('day');
              }}
            />
          </div>
        )}

        {/* Appointment Detail Modal */}
        {selectedAppointment && selectedPatient && (
          <div className="fixed inset-0 bg-neutral-900/50 flex items-center justify-center z-50 fade-in-element" onClick={() => setSelectedAppointment(null)}>
            <div className="bg-white rounded-xl shadow-2xl w-full max-w-3xl max-h-[90vh] overflow-auto animate-scale-in" onClick={(e) => e.stopPropagation()}>
              <AppointmentDetail
                appointment={selectedAppointment}
                patient={selectedPatient}
                onEdit={(appointmentData) => handleUpdateAppointment(selectedAppointment.id, appointmentData)}
                onCancel={() => handleCancelAppointment(selectedAppointment.id)}
                onUpdateStatus={(status) => handleUpdateAppointmentStatus(selectedAppointment.id, status)}
                onDelete={() => handleDeleteAppointment(selectedAppointment.id)}
                onClose={() => setSelectedAppointment(null)}
                isLoading={appointmentLoading}
              />
            </div>
          </div>
        )}

        {/* New Appointment Form Modal */}
        {showNewAppointmentForm && (
          <div className="fixed inset-0 bg-neutral-900/50 flex items-center justify-center z-50 fade-in-element">
            <div className="bg-white rounded-xl shadow-2xl w-full max-w-4xl max-h-[90vh] overflow-hidden animate-scale-in relative">
              {/* Header */}
              <div className="flex items-center justify-between p-6 border-b border-gray-200">
                <h2 className="text-xl font-semibold text-gray-900">
                  {bookingStep === 'patient' ? 'Select Patient' : 'Book Appointment'}
                </h2>
                <button
                  onClick={closeNewAppointmentModal}
                  className="text-gray-400 hover:text-gray-600 transition-colors"
                >
                  <span className="material-icons-round">close</span>
                </button>
              </div>

              {/* Step indicator */}
              <div className="px-6 py-4 bg-gray-50 border-b border-gray-200">
                <div className="flex items-center space-x-4">
                  <div className={`flex items-center space-x-2 ${bookingStep === 'patient' ? 'text-blue-600' : 'text-gray-400'}`}>
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                      bookingStep === 'patient' ? 'bg-blue-100 text-blue-600' : 'bg-gray-100 text-gray-400'
                    }`}>
                      1
                    </div>
                    <span className="text-sm font-medium">Select Patient</span>
                  </div>
                  <div className="flex-1 h-px bg-gray-200"></div>
                  <div className={`flex items-center space-x-2 ${bookingStep === 'appointment' ? 'text-blue-600' : 'text-gray-400'}`}>
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                      bookingStep === 'appointment' ? 'bg-blue-100 text-blue-600' : 'bg-gray-100 text-gray-400'
                    }`}>
                      2
                    </div>
                    <span className="text-sm font-medium">Book Appointment</span>
                  </div>
                </div>
              </div>

              {/* Content - Fixed height container for consistency */}
              <div className="h-[600px] overflow-y-auto">
                <div className="p-6">
                  {bookingStep === 'patient' && (
                    <div className="min-h-full">
                      {showCreatePatientForm ? (
                        <div>
                          <div className="flex items-center justify-between mb-6">
                            <h3 className="text-lg font-medium text-gray-900">Create New Patient</h3>
                            <button
                              onClick={() => setShowCreatePatientForm(false)}
                              className="text-gray-400 hover:text-gray-600"
                            >
                              <span className="material-icons-round">arrow_back</span>
                            </button>
                          </div>
                          <PatientForm
                            onSubmit={handleCreatePatient}
                            onCancel={() => setShowCreatePatientForm(false)}
                          />
                        </div>
                      ) : (
                        <div className="h-full flex flex-col">
                          {/* Search patients */}
                          <div className="mb-6">
                            <div className="relative">
                              <input
                                type="text"
                                placeholder="Search patients by name, email, or phone..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                              />
                              <span className="absolute left-3 top-1/2 transform -translate-y-1/2 material-icons-round text-gray-400">
                                search
                              </span>
                            </div>
                          </div>

                          {/* Create new patient button */}
                          <div className="mb-6">
                            <button
                              onClick={() => setShowCreatePatientForm(true)}
                              className="w-full p-4 border-2 border-dashed border-gray-300 rounded-lg text-gray-600 hover:border-blue-400 hover:text-blue-600 transition-colors"
                            >
                              <span className="material-icons-round mb-2">add</span>
                              <div className="text-sm font-medium">Create New Patient</div>
                            </button>
                          </div>

                          {/* Patient list - flexible height */}
                          <div className="flex-1 min-h-0">
                            <div className="space-y-3 h-full overflow-y-auto">
                              {patients.map((patient) => (
                                <div
                                  key={patient.id}
                                  onClick={() => handlePatientSelect(patient)}
                                  className="p-4 border border-gray-200 rounded-lg hover:border-blue-300 hover:bg-blue-50 cursor-pointer transition-colors"
                                >
                                  <div className="flex items-center justify-between">
                                    <div>
                                      <h4 className="font-medium text-gray-900">
                                        {patient.first_name} {patient.last_name}
                                      </h4>
                                      <p className="text-sm text-gray-600">{patient.email}</p>
                                      {patient.phone && (
                                        <p className="text-sm text-gray-600">{patient.phone}</p>
                                      )}
                                    </div>
                                    <span className="material-icons-round text-gray-400">chevron_right</span>
                                  </div>
                                </div>
                              ))}
                              {patients.length === 0 && searchQuery && (
                                <div className="text-center py-8 text-gray-500">
                                  No patients found matching "{searchQuery}"
                                </div>
                              )}
                              {patients.length === 0 && !searchQuery && (
                                <div className="text-center py-8 text-gray-500">
                                  No patients available. Create a new patient to get started.
                                </div>
                              )}
                            </div>
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {bookingStep === 'appointment' && selectedPatientForBooking && (
                    <div className="min-h-full">
                      <div className="flex items-center justify-between mb-6">
                        <div>
                          <h3 className="text-lg font-medium text-gray-900">Book Appointment</h3>
                          <p className="text-sm text-gray-600">
                            for {selectedPatientForBooking.first_name} {selectedPatientForBooking.last_name}
                          </p>
                        </div>
                        <button
                          onClick={() => setBookingStep('patient')}
                          className="text-gray-400 hover:text-gray-600"
                        >
                          <span className="material-icons-round">arrow_back</span>
                        </button>
                      </div>
                      <AppointmentBookingForm
                        patient={selectedPatientForBooking}
                        onSubmit={handleAppointmentBookingComplete}
                        onCancel={closeNewAppointmentModal}
                        isLoading={appointmentLoading}
                        preselectedDate={selectedTimeSlot?.date}
                        preselectedTime={selectedTimeSlot?.time}
                        preselectedEndTime={selectedTimeSlot?.endTime}
                      />
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Reschedule Confirmation Modal */}
        {showRescheduleConfirmation && rescheduleData && (
          <RescheduleConfirmation
            appointment={rescheduleData.appointment}
            patient={rescheduleData.patient}
            newDate={rescheduleData.newDate}
            newStartTime={rescheduleData.newStartTime}
            newEndTime={rescheduleData.newEndTime}
            onConfirm={handleRescheduleConfirm}
            onCancel={handleRescheduleCancel}
            isLoading={appointmentLoading}
          />
        )}
      </div>
    </DashboardLayout>
  );
};

import React, { useState, useEffect } from 'react';
import { DashboardLayout } from '../components/layout/DashboardLayout';
import { useTranslation } from '../context/TranslationContext';
import { useNotification } from '../context/NotificationContext';
import { Button } from '../components/common/Button';
import { Input } from '../components/common/Input';
import { Settings as SettingsType, Country, Language } from '../types/Settings';
import { SettingsService } from '../services/settingsService';

const LANGUAGES: Language[] = [
  { code: 'en', name: 'English', nativeName: 'English' },
  { code: 'fr', name: 'French', nativeName: 'Français' },
  { code: 'ar', name: 'Arabic', nativeName: 'العربية' },
];

const COUNTRIES: Country[] = [
  {
    code: 'US',
    name: 'United States',
    cities: ['New York', 'Los Angeles', 'Chicago', 'Houston', 'Phoenix']
  },
  {
    code: 'CA',
    name: 'Canada',
    cities: ['Toronto', 'Vancouver', 'Montreal', 'Calgary', 'Ottawa']
  },
  {
    code: 'FR',
    name: 'France',
    cities: ['Paris', 'Lyon', 'Marseille', 'Toulouse', 'Nice']
  },
  {
    code: 'MA',
    name: 'Morocco',
    cities: ['Casablanca', 'Rabat', 'Marrakech', 'Fes', 'Tangier']
  }
];

const TIMEZONES = [
  'UTC-12:00', 'UTC-11:00', 'UTC-10:00', 'UTC-09:00', 'UTC-08:00',
  'UTC-07:00', 'UTC-06:00', 'UTC-05:00', 'UTC-04:00', 'UTC-03:00',
  'UTC-02:00', 'UTC-01:00', 'UTC+00:00', 'UTC+01:00', 'UTC+02:00',
  'UTC+03:00', 'UTC+04:00', 'UTC+05:00', 'UTC+06:00', 'UTC+07:00',
  'UTC+08:00', 'UTC+09:00', 'UTC+10:00', 'UTC+11:00', 'UTC+12:00'
];

const WORKING_DAYS = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];

export const SettingsPage: React.FC = () => {
  const { t } = useTranslation();
  const { showNotification } = useNotification();

  const [settings, setSettings] = useState<SettingsType>({
    // Localization
    language: 'en',
    country: 'US',
    city: 'New York',
    timezone: 'UTC-05:00',

    // Clinic Information
    clinicName: 'Imarisys Clinic',
    clinicAddress: '123 Medical Center Dr, New York, NY 10001',
    clinicPhone: '+1 (555) 123-4567',
    clinicEmail: 'info@imarisys.com',

    // Appointment Settings
    appointmentDuration: 30,
    workingHoursStart: '09:00',
    workingHoursEnd: '17:00',
    workingDays: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],

    // Notification Settings
    emailNotifications: true,
    smsNotifications: false,
    appointmentReminders: true,
    reminderTimeBefore: 60,

    // Display Settings
    dateFormat: 'MM/DD/YYYY',
    timeFormat: '12h',
    currency: 'USD',

    // Weather Settings
    showWeather: true,
    temperatureUnit: 'fahrenheit'
  });

  const [isSaving, setIsSaving] = useState(false);
  const [activeTab, setActiveTab] = useState('general');
  const [isLoading, setIsLoading] = useState(true);

  const selectedCountry = COUNTRIES.find(country => country.code === settings.country);

  useEffect(() => {
    loadSettings();
  }, []);

  const loadSettings = async () => {
    try {
      setIsLoading(true);
      const response = await SettingsService.getSettings();
      setSettings(response.data);
    } catch (error) {
      showNotification('error', 'Error', 'Failed to load settings');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field: keyof SettingsType, value: any) => {
    setSettings(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleWorkingDayToggle = (day: string) => {
    const updatedDays = settings.workingDays.includes(day)
      ? settings.workingDays.filter(d => d !== day)
      : [...settings.workingDays, day];

    handleInputChange('workingDays', updatedDays);
  };

  const handleSave = async () => {
    setIsSaving(true);
    try {
      await SettingsService.updateSettings(settings);
      showNotification('success', 'Success', 'Settings saved successfully');
    } catch (error) {
      showNotification('error', 'Error', 'Failed to save settings');
    } finally {
      setIsSaving(false);
    }
  };

  const handleExportSettings = async () => {
    try {
      const blob = await SettingsService.exportSettings();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `clinic-settings-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
      showNotification('success', 'Success', 'Settings exported successfully');
    } catch (error) {
      showNotification('error', 'Error', 'Failed to export settings');
    }
  };

  const handleImportSettings = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    try {
      const response = await SettingsService.importSettings(file);
      setSettings(response.data);
      showNotification('success', 'Success', 'Settings imported successfully');
    } catch (error) {
      showNotification('error', 'Error', 'Failed to import settings');
    }

    // Reset input
    event.target.value = '';
  };

  const renderGeneralTab = () => (
    <div className="space-y-6">
      {/* Localization Section */}
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">language</span>
          {t('localization')}
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('language')}</label>
            <select
              value={settings.language}
              onChange={(e) => handleInputChange('language', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {LANGUAGES.map(lang => (
                <option key={lang.code} value={lang.code}>
                  {lang.name} ({lang.nativeName})
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('country')}</label>
            <select
              value={settings.country}
              onChange={(e) => {
                handleInputChange('country', e.target.value);
                const newCountry = COUNTRIES.find(c => c.code === e.target.value);
                if (newCountry && newCountry.cities.length > 0) {
                  handleInputChange('city', newCountry.cities[0]);
                }
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {COUNTRIES.map(country => (
                <option key={country.code} value={country.code}>
                  {country.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('city')}</label>
            <select
              value={settings.city}
              onChange={(e) => handleInputChange('city', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {selectedCountry?.cities.map(city => (
                <option key={city} value={city}>
                  {city}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('timezone')}</label>
            <select
              value={settings.timezone}
              onChange={(e) => handleInputChange('timezone', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {TIMEZONES.map(tz => (
                <option key={tz} value={tz}>
                  {tz}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Clinic Information Section */}
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">local_hospital</span>
          {t('clinic_information')}
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="md:col-span-2">
            <Input
              label={t('clinic_name')}
              value={settings.clinicName}
              onChange={(e) => handleInputChange('clinicName', e.target.value)}
              placeholder={t('enter_clinic_name')}
            />
          </div>

          <div className="md:col-span-2">
            <Input
              label={t('clinic_address')}
              value={settings.clinicAddress}
              onChange={(e) => handleInputChange('clinicAddress', e.target.value)}
              placeholder={t('enter_clinic_address')}
            />
          </div>

          <div>
            <Input
              label={t('clinic_phone')}
              value={settings.clinicPhone}
              onChange={(e) => handleInputChange('clinicPhone', e.target.value)}
              placeholder={t('enter_phone_number')}
            />
          </div>

          <div>
            <Input
              label={t('clinic_email')}
              type="email"
              value={settings.clinicEmail}
              onChange={(e) => handleInputChange('clinicEmail', e.target.value)}
              placeholder={t('enter_email_address')}
            />
          </div>
        </div>
      </div>

      {/* Import/Export Section */}
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">import_export</span>
          Backup & Restore
        </h3>

        <div className="flex flex-col sm:flex-row gap-4">
          <Button
            onClick={handleExportSettings}
            variant="secondary"
            className="flex items-center justify-center"
          >
            <span className="material-icons-round mr-2">download</span>
            Export Settings
          </Button>

          <label className="cursor-pointer">
            <input
              type="file"
              accept=".json"
              onChange={handleImportSettings}
              className="hidden"
            />
            <div className="px-4 py-2 bg-gray-100 hover:bg-gray-200 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 cursor-pointer transition-colors flex items-center justify-center">
              <span className="material-icons-round mr-2">upload</span>
              Import Settings
            </div>
          </label>
        </div>

        <p className="text-sm text-gray-600 mt-2">
          Export your settings as a backup or import previously saved settings.
        </p>
      </div>
    </div>
  );

  const renderAppointmentsTab = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">schedule</span>
          {t('working_hours')}
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('default_appointment_duration')}</label>
            <select
              value={settings.appointmentDuration}
              onChange={(e) => handleInputChange('appointmentDuration', parseInt(e.target.value))}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value={15}>15 {t('minutes')}</option>
              <option value={30}>30 {t('minutes')}</option>
              <option value={45}>45 {t('minutes')}</option>
              <option value={60}>60 {t('minutes')}</option>
            </select>
          </div>

          <div>
            <Input
              label={t('start_time')}
              type="time"
              value={settings.workingHoursStart}
              onChange={(e) => handleInputChange('workingHoursStart', e.target.value)}
            />
          </div>

          <div>
            <Input
              label={t('end_time')}
              type="time"
              value={settings.workingHoursEnd}
              onChange={(e) => handleInputChange('workingHoursEnd', e.target.value)}
            />
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-3">{t('working_days')}</label>
          <div className="flex flex-wrap gap-2">
            {WORKING_DAYS.map(day => (
              <button
                key={day}
                onClick={() => handleWorkingDayToggle(day)}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                  settings.workingDays.includes(day)
                    ? 'bg-primary-100 text-primary-700 border-2 border-primary-300'
                    : 'bg-gray-100 text-gray-600 border-2 border-gray-200 hover:bg-gray-200'
                }`}
              >
                {day.charAt(0).toUpperCase() + day.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  const renderNotificationsTab = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">notifications</span>
          Notification Preferences
        </h3>

        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium text-gray-800">Email Notifications</h4>
              <p className="text-sm text-gray-600">Receive notifications via email</p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={settings.emailNotifications}
                onChange={(e) => handleInputChange('emailNotifications', e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
            </label>
          </div>

          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium text-gray-800">SMS Notifications</h4>
              <p className="text-sm text-gray-600">Receive notifications via SMS</p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={settings.smsNotifications}
                onChange={(e) => handleInputChange('smsNotifications', e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
            </label>
          </div>

          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium text-gray-800">Appointment Reminders</h4>
              <p className="text-sm text-gray-600">Send reminders before appointments</p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={settings.appointmentReminders}
                onChange={(e) => handleInputChange('appointmentReminders', e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
            </label>
          </div>

          {settings.appointmentReminders && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Reminder Time</label>
              <select
                value={settings.reminderTimeBefore}
                onChange={(e) => handleInputChange('reminderTimeBefore', parseInt(e.target.value))}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              >
                <option value={15}>15 minutes before</option>
                <option value={30}>30 minutes before</option>
                <option value={60}>1 hour before</option>
                <option value={120}>2 hours before</option>
                <option value={1440}>1 day before</option>
              </select>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const renderDisplayTab = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">display_settings</span>
          Display Preferences
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Date Format</label>
            <select
              value={settings.dateFormat}
              onChange={(e) => handleInputChange('dateFormat', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="MM/DD/YYYY">MM/DD/YYYY</option>
              <option value="DD/MM/YYYY">DD/MM/YYYY</option>
              <option value="YYYY-MM-DD">YYYY-MM-DD</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Time Format</label>
            <select
              value={settings.timeFormat}
              onChange={(e) => handleInputChange('timeFormat', e.target.value as '12h' | '24h')}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="12h">12 Hour (AM/PM)</option>
              <option value="24h">24 Hour</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Currency</label>
            <select
              value={settings.currency}
              onChange={(e) => handleInputChange('currency', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="USD">USD ($)</option>
              <option value="EUR">EUR (€)</option>
              <option value="GBP">GBP (£)</option>
              <option value="MAD">MAD (د.م.)</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Temperature Unit</label>
            <select
              value={settings.temperatureUnit}
              onChange={(e) => handleInputChange('temperatureUnit', e.target.value as 'celsius' | 'fahrenheit')}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="celsius">Celsius (°C)</option>
              <option value="fahrenheit">Fahrenheit (°F)</option>
            </select>
          </div>
        </div>

        <div className="mt-6">
          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium text-gray-800">Show Weather Widget</h4>
              <p className="text-sm text-gray-600">Display weather information on dashboard</p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={settings.showWeather}
                onChange={(e) => handleInputChange('showWeather', e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
            </label>
          </div>
        </div>
      </div>
    </div>
  );

  const tabs = [
    { id: 'general', label: 'General', icon: 'settings' },
    { id: 'appointments', label: 'Appointments', icon: 'event' },
    { id: 'notifications', label: 'Notifications', icon: 'notifications' },
    { id: 'display', label: 'Display', icon: 'display_settings' }
  ];

  if (isLoading) {
    return (
      <DashboardLayout>
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <span className="material-icons-round animate-spin text-4xl text-primary-600 mb-4">autorenew</span>
            <p className="text-gray-600">Loading settings...</p>
          </div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">{t('settings')}</h1>
            <p className="text-gray-600 mt-1">{t('manage_clinic_preferences')}</p>
          </div>

          <Button
            onClick={handleSave}
            disabled={isSaving}
            className="bg-primary-600 hover:bg-primary-700"
          >
            {isSaving ? (
              <>
                <span className="material-icons-round animate-spin mr-2">autorenew</span>
                {t('saving')}
              </>
            ) : (
              <>
                <span className="material-icons-round mr-2">save</span>
                {t('save_changes')}
              </>
            )}
          </Button>
        </div>

        {/* Tabs */}
        <div className="bg-white rounded-2xl shadow-card">
          <div className="border-b border-gray-200">
            <nav className="flex space-x-8 px-6">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                    activeTab === tab.id
                      ? 'border-primary-500 text-primary-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <span className="material-icons-round mr-2 text-lg">{tab.icon}</span>
                  {t(tab.label.toLowerCase())}
                </button>
              ))}
            </nav>
          </div>

          <div className="p-6">
            {activeTab === 'general' && renderGeneralTab()}
            {activeTab === 'appointments' && renderAppointmentsTab()}
            {activeTab === 'notifications' && renderNotificationsTab()}
            {activeTab === 'display' && renderDisplayTab()}
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
};

import React, { useState, useEffect } from 'react';
import { DashboardLayout } from '../components/layout/DashboardLayout';
import { useTranslation } from '../context/TranslationContext';
import { useNotification } from '../context/NotificationContext';
import { Button } from '../components/common/Button';
import { Input } from '../components/common/Input';
import { Settings as SettingsType, Country, Language } from '../types/Settings';
import { SettingsService } from '../services/settingsService';

const LANGUAGES: Language[] = [
  { code: 'en', name: 'English', nativeName: 'English' },
  { code: 'fr', name: 'French', nativeName: 'Français' },
  { code: 'ar', name: 'Arabic', nativeName: 'العربية' },
];

const COUNTRIES: Country[] = [
  {
    code: 'US',
    name: 'United States',
    cities: ['New York', 'Los Angeles', 'Chicago', 'Houston', 'Phoenix']
  },
  {
    code: 'CA',
    name: 'Canada',
    cities: ['Toronto', 'Vancouver', 'Montreal', 'Calgary', 'Ottawa']
  },
  {
    code: 'FR',
    name: 'France',
    cities: ['Paris', 'Lyon', 'Marseille', 'Toulouse', 'Nice']
  },
  {
    code: 'MA',
    name: 'Morocco',
    cities: ['Casablanca', 'Rabat', 'Marrakech', 'Fes', 'Tangier']
  }
];

const TIMEZONES = [
  'UTC-12:00', 'UTC-11:00', 'UTC-10:00', 'UTC-09:00', 'UTC-08:00',
  'UTC-07:00', 'UTC-06:00', 'UTC-05:00', 'UTC-04:00', 'UTC-03:00',
  'UTC-02:00', 'UTC-01:00', 'UTC+00:00', 'UTC+01:00', 'UTC+02:00',
  'UTC+03:00', 'UTC+04:00', 'UTC+05:00', 'UTC+06:00', 'UTC+07:00',
  'UTC+08:00', 'UTC+09:00', 'UTC+10:00', 'UTC+11:00', 'UTC+12:00'
];

const WORKING_DAYS = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];

export const SettingsPage: React.FC = () => {
  const { t } = useTranslation();
  const { showNotification } = useNotification();

  const [settings, setSettings] = useState<SettingsType>({
    // Localization
    language: 'en',
    country: 'US',
    city: 'New York',
    timezone: 'UTC-05:00',

    // Clinic Information
    clinicName: 'Imarisys Clinic',
    clinicAddress: '123 Medical Center Dr, New York, NY 10001',
    clinicPhone: '+1 (555) 123-4567',
    clinicEmail: 'info@imarisys.com',

    // Appointment Settings
    appointmentDuration: 30,
    workingHoursStart: '09:00',
    workingHoursEnd: '17:00',
    workingDays: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],

    // Notification Settings
    emailNotifications: true,
    smsNotifications: false,
    appointmentReminders: true,
    reminderTimeBefore: 60,

    // Display Settings
    dateFormat: 'MM/DD/YYYY',
    timeFormat: '12h',
    currency: 'USD',

    // Weather Settings
    showWeather: true,
    temperatureUnit: 'fahrenheit'
  });

  const [isSaving, setIsSaving] = useState(false);
  const [activeTab, setActiveTab] = useState('general');
  const [isLoading, setIsLoading] = useState(true);

  const selectedCountry = COUNTRIES.find(country => country.code === settings.country);

  useEffect(() => {
    loadSettings();
  }, []);

  const loadSettings = async () => {
    try {
      setIsLoading(true);
      const response = await SettingsService.getSettings();
      setSettings(response.data);
    } catch (error) {
      showNotification('error', 'Error', 'Failed to load settings');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field: keyof SettingsType, value: any) => {
    setSettings(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleWorkingDayToggle = (day: string) => {
    const updatedDays = settings.workingDays.includes(day)
      ? settings.workingDays.filter(d => d !== day)
      : [...settings.workingDays, day];

    handleInputChange('workingDays', updatedDays);
  };

  const handleSave = async () => {
    setIsSaving(true);
    try {
      await SettingsService.updateSettings(settings);
      showNotification('success', 'Success', 'Settings saved successfully');
    } catch (error) {
      showNotification('error', 'Error', 'Failed to save settings');
    } finally {
      setIsSaving(false);
    }
  };

  const handleExportSettings = async () => {
    try {
      const blob = await SettingsService.exportSettings();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `clinic-settings-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
      showNotification('success', 'Success', 'Settings exported successfully');
    } catch (error) {
      showNotification('error', 'Error', 'Failed to export settings');
    }
  };

  const handleImportSettings = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    try {
      const response = await SettingsService.importSettings(file);
      setSettings(response.data);
      showNotification('success', 'Success', 'Settings imported successfully');
    } catch (error) {
      showNotification('error', 'Error', 'Failed to import settings');
    }

    // Reset input
    event.target.value = '';
  };

  const renderGeneralTab = () => (
    <div className="space-y-6">
      {/* Localization Section */}
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">language</span>
          {t('localization')}
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('language')}</label>
            <select
              value={settings.language}
              onChange={(e) => handleInputChange('language', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {LANGUAGES.map(lang => (
                <option key={lang.code} value={lang.code}>
                  {lang.name} ({lang.nativeName})
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('country')}</label>
            <select
              value={settings.country}
              onChange={(e) => {
                handleInputChange('country', e.target.value);
                const newCountry = COUNTRIES.find(c => c.code === e.target.value);
                if (newCountry && newCountry.cities.length > 0) {
                  handleInputChange('city', newCountry.cities[0]);
                }
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {COUNTRIES.map(country => (
                <option key={country.code} value={country.code}>
                  {country.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('city')}</label>
            <select
              value={settings.city}
              onChange={(e) => handleInputChange('city', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {selectedCountry?.cities.map(city => (
                <option key={city} value={city}>
                  {city}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('timezone')}</label>
            <select
              value={settings.timezone}
              onChange={(e) => handleInputChange('timezone', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {TIMEZONES.map(tz => (
                <option key={tz} value={tz}>
                  {tz}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Clinic Information Section */}
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">local_hospital</span>
          {t('clinic_information')}
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="md:col-span-2">
            <Input
              label={t('clinic_name')}
              value={settings.clinicName}
              onChange={(e) => handleInputChange('clinicName', e.target.value)}
              placeholder={t('enter_clinic_name')}
            />
          </div>

          <div className="md:col-span-2">
            <Input
              label={t('clinic_address')}
              value={settings.clinicAddress}
              onChange={(e) => handleInputChange('clinicAddress', e.target.value)}
              placeholder={t('enter_clinic_address')}
            />
          </div>

          <div>
            <Input
              label={t('clinic_phone')}
              value={settings.clinicPhone}
              onChange={(e) => handleInputChange('clinicPhone', e.target.value)}
              placeholder={t('enter_phone_number')}
            />
          </div>

          <div>
            <Input
              label={t('clinic_email')}
              type="email"
              value={settings.clinicEmail}
              onChange={(e) => handleInputChange('clinicEmail', e.target.value)}
              placeholder={t('enter_email_address')}
            />
          </div>
        </div>
      </div>

      {/* Import/Export Section */}
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">import_export</span>
          Backup & Restore
        </h3>

        <div className="flex flex-col sm:flex-row gap-4">
          <Button
            onClick={handleExportSettings}
            variant="secondary"
            className="flex items-center justify-center"
          >
            <span className="material-icons-round mr-2">download</span>
            Export Settings
          </Button>

          <label className="cursor-pointer">
            <input
              type="file"
              accept=".json"
              onChange={handleImportSettings}
              className="hidden"
            />
            <div className="px-4 py-2 bg-gray-100 hover:bg-gray-200 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 cursor-pointer transition-colors flex items-center justify-center">
              <span className="material-icons-round mr-2">upload</span>
              Import Settings
            </div>
          </label>
        </div>

        <p className="text-sm text-gray-600 mt-2">
          Export your settings as a backup or import previously saved settings.
        </p>
      </div>
    </div>
  );

  const renderAppointmentsTab = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">schedule</span>
          {t('working_hours')}
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{t('default_appointment_duration')}</label>
            <select
              value={settings.appointmentDuration}
              onChange={(e) => handleInputChange('appointmentDuration', parseInt(e.target.value))}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value={15}>15 {t('minutes')}</option>
              <option value={30}>30 {t('minutes')}</option>
              <option value={45}>45 {t('minutes')}</option>
              <option value={60}>60 {t('minutes')}</option>
            </select>
          </div>

          <div>
            <Input
              label={t('start_time')}
              type="time"
              value={settings.workingHoursStart}
              onChange={(e) => handleInputChange('workingHoursStart', e.target.value)}
            />
          </div>

          <div>
            <Input
              label={t('end_time')}
              type="time"
              value={settings.workingHoursEnd}
              onChange={(e) => handleInputChange('workingHoursEnd', e.target.value)}
            />
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-3">{t('working_days')}</label>
          <div className="flex flex-wrap gap-2">
            {WORKING_DAYS.map(day => (
              <button
                key={day}
                onClick={() => handleWorkingDayToggle(day)}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                  settings.workingDays.includes(day)
                    ? 'bg-primary-100 text-primary-700 border-2 border-primary-300'
                    : 'bg-gray-100 text-gray-600 border-2 border-gray-200 hover:bg-gray-200'
                }`}
              >
                {day.charAt(0).toUpperCase() + day.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  const renderNotificationsTab = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">notifications</span>
          Notification Preferences
        </h3>

        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium text-gray-800">Email Notifications</h4>
              <p className="text-sm text-gray-600">Receive notifications via email</p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={settings.emailNotifications}
                onChange={(e) => handleInputChange('emailNotifications', e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
            </label>
          </div>

          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium text-gray-800">SMS Notifications</h4>
              <p className="text-sm text-gray-600">Receive notifications via SMS</p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={settings.smsNotifications}
                onChange={(e) => handleInputChange('smsNotifications', e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
            </label>
          </div>

          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium text-gray-800">Appointment Reminders</h4>
              <p className="text-sm text-gray-600">Send reminders before appointments</p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={settings.appointmentReminders}
                onChange={(e) => handleInputChange('appointmentReminders', e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
            </label>
          </div>

          {settings.appointmentReminders && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Reminder Time</label>
              <select
                value={settings.reminderTimeBefore}
                onChange={(e) => handleInputChange('reminderTimeBefore', parseInt(e.target.value))}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              >
                <option value={15}>15 minutes before</option>
                <option value={30}>30 minutes before</option>
                <option value={60}>1 hour before</option>
                <option value={120}>2 hours before</option>
                <option value={1440}>1 day before</option>
              </select>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const renderDisplayTab = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-2xl shadow-card p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
          <span className="material-icons-round text-primary-600 mr-2">display_settings</span>
          Display Preferences
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Date Format</label>
            <select
              value={settings.dateFormat}
              onChange={(e) => handleInputChange('dateFormat', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="MM/DD/YYYY">MM/DD/YYYY</option>
              <option value="DD/MM/YYYY">DD/MM/YYYY</option>
              <option value="YYYY-MM-DD">YYYY-MM-DD</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Time Format</label>
            <select
              value={settings.timeFormat}
              onChange={(e) => handleInputChange('timeFormat', e.target.value as '12h' | '24h')}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="12h">12 Hour (AM/PM)</option>
              <option value="24h">24 Hour</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Currency</label>
            <select
              value={settings.currency}
              onChange={(e) => handleInputChange('currency', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="USD">USD ($)</option>
              <option value="EUR">EUR (€)</option>
              <option value="GBP">GBP (£)</option>
              <option value="MAD">MAD (د.م.)</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Temperature Unit</label>
            <select
              value={settings.temperatureUnit}
              onChange={(e) => handleInputChange('temperatureUnit', e.target.value as 'celsius' | 'fahrenheit')}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="celsius">Celsius (°C)</option>
              <option value="fahrenheit">Fahrenheit (°F)</option>
            </select>
          </div>
        </div>

        <div className="mt-6">
          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium text-gray-800">Show Weather Widget</h4>
              <p className="text-sm text-gray-600">Display weather information on dashboard</p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={settings.showWeather}
                onChange={(e) => handleInputChange('showWeather', e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
            </label>
          </div>
        </div>
      </div>
    </div>
  );

  const tabs = [
    { id: 'general', label: 'General', icon: 'settings' },
    { id: 'appointments', label: 'Appointments', icon: 'event' },
    { id: 'notifications', label: 'Notifications', icon: 'notifications' },
    { id: 'display', label: 'Display', icon: 'display_settings' }
  ];

  if (isLoading) {
    return (
      <DashboardLayout>
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <span className="material-icons-round animate-spin text-4xl text-primary-600 mb-4">autorenew</span>
            <p className="text-gray-600">Loading settings...</p>
          </div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">{t('settings')}</h1>
            <p className="text-gray-600 mt-1">{t('manage_clinic_preferences')}</p>
          </div>

          <Button
            onClick={handleSave}
            disabled={isSaving}
            className="bg-primary-600 hover:bg-primary-700"
          >
            {isSaving ? (
              <>
                <span className="material-icons-round animate-spin mr-2">autorenew</span>
                {t('saving')}
              </>
            ) : (
              <>
                <span className="material-icons-round mr-2">save</span>
                {t('save_changes')}
              </>
            )}
          </Button>
        </div>

        {/* Tabs */}
        <div className="bg-white rounded-2xl shadow-card">
          <div className="border-b border-gray-200">
            <nav className="flex space-x-8 px-6">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                    activeTab === tab.id
                      ? 'border-primary-500 text-primary-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <span className="material-icons-round mr-2 text-lg">{tab.icon}</span>
                  {tab.label}
                </button>
              ))}
            </nav>
          </div>

          <div className="p-6">
            {activeTab === 'general' && renderGeneralTab()}
            {activeTab === 'appointments' && renderAppointmentsTab()}
            {activeTab === 'notifications' && renderNotificationsTab()}
            {activeTab === 'display' && renderDisplayTab()}
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
};

import { API_CONFIG, buildApiUrl } from '../config/api';
import { Patient, PatientCreate, PatientUpdate, PatientRead, PatientListResponse, PatientSummary } from '../types/Patient';

// Helper function to extract error message from response
const extractErrorMessage = async (response: Response): Promise<string> => {
  try {
    const errorData = await response.json();

    // Handle different error response formats
    if (errorData.detail) {
      return errorData.detail;
    }

    if (errorData.message) {
      return errorData.message;
    }

    if (errorData.error) {
      return errorData.error;
    }

    // Handle validation errors (array format)
    if (Array.isArray(errorData) && errorData.length > 0) {
      return errorData.map(err => err.msg || err.message || err).join(', ');
    }

    // Handle field-specific errors
    if (typeof errorData === 'object') {
      const fieldErrors = Object.entries(errorData)
        .map(([field, error]) => `${field}: ${error}`)
        .join(', ');
      if (fieldErrors) return fieldErrors;
    }

    return `HTTP ${response.status}: ${response.statusText}`;
  } catch {
    // If response is not JSON, return status text
    return `HTTP ${response.status}: ${response.statusText}`;
  }
};

// API service for patient operations
export class PatientService {
  // List patients with pagination
  static async listPatients(offset = 0, limit = 20): Promise<PatientListResponse> {
    try {
      const url = buildApiUrl(`${API_CONFIG.endpoints.patients.list}?offset=${offset}&limit=${limit}`);
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while loading patients');
    }
  }

  // Search patients by query (first name, last name, phone, email)
  static async searchPatients(query: string, offset = 0, limit = 100): Promise<PatientListResponse> {
    try {
      const encodedQuery = encodeURIComponent(query);
      const url = buildApiUrl(`${API_CONFIG.endpoints.patients.search}?term=${encodedQuery}&offset=${offset}&limit=${limit}`);
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while searching patients');
    }
  }

  // Get patient summary statistics
  static async getPatientSummary(): Promise<PatientSummary> {
    try {
      const url = buildApiUrl(API_CONFIG.endpoints.patients.summary);
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while fetching patient summary');
    }
  }

import { API_CONFIG, buildApiUrl } from '../config/api';
import { Patient, PatientCreate, PatientUpdate, PatientRead, PatientListResponse, PatientSummary, PatientWithAppointments } from '../types/Patient';

// Helper function to extract error message from response
const extractErrorMessage = async (response: Response): Promise<string> => {
  try {
    const errorData = await response.json();

    // Handle different error response formats
    if (errorData.detail) {
      return errorData.detail;
    }

    if (errorData.message) {
      return errorData.message;
    }

    if (errorData.error) {
      return errorData.error;
    }

    // Handle validation errors (array format)
    if (Array.isArray(errorData) && errorData.length > 0) {
      return errorData.map(err => err.msg || err.message || err).join(', ');
    }

    // Handle field-specific errors
    if (typeof errorData === 'object') {
      const fieldErrors = Object.entries(errorData)
        .map(([field, error]) => `${field}: ${error}`)
        .join(', ');
      if (fieldErrors) return fieldErrors;
    }

    return `HTTP ${response.status}: ${response.statusText}`;
  } catch {
    // If response is not JSON, return status text
    return `HTTP ${response.status}: ${response.statusText}`;
  }
};

// API service for patient operations
export class PatientService {
  // List patients with pagination
  static async listPatients(offset = 0, limit = 20): Promise<PatientListResponse> {
    try {
      const url = buildApiUrl(`${API_CONFIG.endpoints.patients.list}?offset=${offset}&limit=${limit}`);
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while loading patients');
    }
  }

  // Search patients by query (first name, last name, phone, email)
  static async searchPatients(query: string, offset = 0, limit = 100): Promise<PatientListResponse> {
    try {
      const encodedQuery = encodeURIComponent(query);
      const url = buildApiUrl(`${API_CONFIG.endpoints.patients.search}?term=${encodedQuery}&offset=${offset}&limit=${limit}`);
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while searching patients');
    }
  }

  // Get patient summary statistics
  static async getPatientSummary(): Promise<PatientSummary> {
    try {
      const url = buildApiUrl(API_CONFIG.endpoints.patients.summary);
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while fetching patient summary');
    }
  }

  // Export patients as Excel file
  static async exportPatients(): Promise<void> {
    try {
      const url = buildApiUrl(API_CONFIG.endpoints.patients.export);
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      // Get the filename from the response headers or use a default
      const contentDisposition = response.headers.get('Content-Disposition');
      let filename = 'patients_export.xlsx';
      if (contentDisposition) {
        const filenameMatch = contentDisposition.match(/filename="(.+)"/);
        if (filenameMatch) {
          filename = filenameMatch[1];
        }
      }

      // Get the blob data
      const blob = await response.blob();

      // Create a download link and trigger the download
      const downloadUrl = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = filename;
      document.body.appendChild(link);
      link.click();

      // Clean up
      document.body.removeChild(link);
      window.URL.revokeObjectURL(downloadUrl);
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while exporting patients');
    }
  }

  // Get a single patient by ID
  static async getPatient(patientId: string): Promise<PatientRead> {
    try {
      const url = buildApiUrl(API_CONFIG.endpoints.patients.get(patientId));
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while fetching patient');
    }
  }

  // Create a new patient
  static async createPatient(patientData: PatientCreate): Promise<PatientRead> {
    try {
      const url = buildApiUrl(API_CONFIG.endpoints.patients.create);
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(patientData),
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while creating patient');
    }
  }

  // Update an existing patient
  static async updatePatient(patientId: string, patientData: PatientUpdate): Promise<PatientRead> {
    try {
      const url = buildApiUrl(API_CONFIG.endpoints.patients.update(patientId));
      const response = await fetch(url, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(patientData),
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while updating patient');
    }
  }

  // Delete a patient
  static async deletePatient(patientId: string): Promise<void> {
    try {
      const url = buildApiUrl(API_CONFIG.endpoints.patients.delete(patientId));
      const response = await fetch(url, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while deleting patient');
    }
  }

  // Get a single patient by ID with appointments
  static async getPatientWithAppointments(patientId: string): Promise<PatientWithAppointments> {
    try {
      const url = buildApiUrl(API_CONFIG.endpoints.patients.get(patientId));
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorMessage = await extractErrorMessage(response);
        throw new Error(errorMessage);
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        throw error;
      }
      throw new Error('Network error occurred while fetching patient with appointments');
    }
  }
}

