option.value:
import React from 'react';
import { Appointment, AppointmentStatus } from '../../types/Appointment';

interface DayViewProps {
  currentDate: Date;
  appointments: Appointment[];
  onTimeSlotClick: (date: string, time: string) => void;
  onAppointmentClick: (appointment: Appointment) => void;
  onAppointmentDrop?: (appointment: Appointment, newDate: string, newTime: string) => void;
  getAppointmentDate: (apt: Appointment) => string;
  formatAppointmentTime: (apt: Appointment) => string;
  getAppointmentDuration: (apt: Appointment) => number;
  getAppointmentBackgroundColor: (status: AppointmentStatus) => string;
  getAppointmentTextColor: (status: AppointmentStatus) => string;
  getPatientName: (apt: Appointment) => string;
  // Add drag and drop props similar to WeeklyView
  handleMouseUp: () => void;
  handleMouseDown: (e: React.MouseEvent, date: string, time: string) => void;
  handleMouseEnter: (date: string, time: string) => void;
  isSlotSelected: (date: string, time: string) => boolean;
  workingHours?: {
    startTime: string;
    endTime: string;
  };
  workingDays?: string[];
}

export const DayView: React.FC<DayViewProps> = ({
  currentDate,
  appointments,
  onTimeSlotClick,
  onAppointmentClick,
  onAppointmentDrop,
  getAppointmentDate,
  formatAppointmentTime,
  getAppointmentDuration,
  getAppointmentBackgroundColor,
  getAppointmentTextColor,
  getPatientName,
  handleMouseUp,
  handleMouseDown,
  handleMouseEnter,
  isSlotSelected,
  workingHours = { startTime: '08:00', endTime: '17:00' },
  workingDays = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'],
}) => {
  const [draggedAppointment, setDraggedAppointment] = React.useState<Appointment | null>(null);
  const [dragPreviewPosition, setDragPreviewPosition] = React.useState<{date: string, time: string} | null>(null);

  // Check if current date is a working day
  const isWorkingDay = (): boolean => {
    const dayName = currentDate.toLocaleDateString('en-US', { weekday: 'long' });
    return workingDays.includes(dayName);
  };

  const isWorking = isWorkingDay();

  // Generate time slots based on working hours
  const generateTimeSlots = () => {
    const startHour = parseInt(workingHours.startTime.split(':')[0]);
    const endHour = parseInt(workingHours.endTime.split(':')[0]);
    const totalHours = endHour - startHour;
    const slotsPerHour = 4; // 15-minute intervals
    const totalSlots = totalHours * slotsPerHour;

    return Array.from({ length: totalSlots }, (_, i) => {
      const hour = Math.floor(i / slotsPerHour) + startHour;
      const minutes = (i % slotsPerHour) * 15;
      return `${hour.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
    });
  };

  const timeSlots = generateTimeSlots();

  // Handle appointment drag start
  const handleAppointmentDragStart = (e: React.DragEvent, appointment: Appointment) => {
    e.stopPropagation();
    setDraggedAppointment(appointment);
    e.dataTransfer.setData('text/plain', appointment.id);
    e.dataTransfer.effectAllowed = 'move';
  };

  // Handle appointment drag end
  const handleAppointmentDragEnd = () => {
    setDraggedAppointment(null);
    setDragPreviewPosition(null);
  };

  // Handle drop on time slot
  const handleTimeSlotDrop = (e: React.DragEvent, date: string, time: string) => {
    e.preventDefault();
    e.stopPropagation();

    if (draggedAppointment && onAppointmentDrop) {
      onAppointmentDrop(draggedAppointment, date, time);
    }
    setDraggedAppointment(null);
    setDragPreviewPosition(null);
  };

  // Handle drag over to allow drop and show preview
  const handleTimeSlotDragOver = (e: React.DragEvent, date: string, time: string) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';

    if (draggedAppointment) {
      setDragPreviewPosition({ date, time });
    }
  };

  // Handle drag leave to hide preview when leaving time slot
  const handleTimeSlotDragLeave = (e: React.DragEvent) => {
    // Only hide preview if we're actually leaving the time slot area
    const rect = (e.currentTarget as HTMLElement).getBoundingClientRect();
    const { clientX, clientY } = e;

    if (clientX < rect.left || clientX > rect.right || clientY < rect.top || clientY > rect.bottom) {
      setDragPreviewPosition(null);
    }
  };

  const dayStr = currentDate.toISOString().split('T')[0];

  return (
    <div className="card overflow-hidden">
      {/* Day header - similar to week header but for a single day */}
      <div className="grid grid-cols-[80px_1fr] border-b border-gray-300">
        <div className="p-2"></div>
        <div
          className={`p-4 text-center slide-up-element ${
            currentDate.toDateString() === new Date().toDateString()
              ? 'bg-primary-50 border-primary-200'
              : 'bg-red-50'
          }`}
          style={{ animationDelay: '0.05s' }}
        >
          <p className={`text-sm ${
            isWorking ? 'text-neutral-500' : 'text-red-400'
          }`}>
            isWorking ? 'text-neutral-500' : 'text-red-400'
              : 'text-red-600'
          }`}>
            {currentDate.getDate()}
          </p>
          {!isWorking && (
              ? 'text-primary-600'
      </div>

      {/* Time slots grid - 15 minute precision with drag and drop */}
      <div
        className="flex-1 overflow-y-auto"
        onMouseUp={handleMouseUp}
          {!isWorking && (
            <p className="text-xs text-red-500 mt-1">Closed</p>
          )}
        onMouseLeave={handleMouseUp}
        style={{ height: 'calc(100vh - 280px)' }}
      >
        {timeSlots.map((time, timeIndex) => {
          const isSelected = isSlotSelected(dayStr, time);
          const dayAppointments = appointments.filter(apt => {
            const aptDate = getAppointmentDate(apt);
            const aptTime = formatAppointmentTime(apt);
            const aptHour = parseInt(aptTime.split(':')[0]);
            const aptMinute = parseInt(aptTime.split(':')[1]);
            const timeHour = parseInt(time.split(':')[0]);
            const timeMinute = parseInt(time.split(':')[1]);

            // Only show appointment in its starting time slot
            return aptDate === dayStr &&
              aptHour === timeHour &&
              aptMinute === timeMinute;
          });

          // Define border classes for visual hierarchy
          let borderBottomClass;
          if (timeIndex % 4 === 3) {
            borderBottomClass = 'border-b border-gray-300'; // Hour boundaries
          } else if (timeIndex % 2 === 1) {
            borderBottomClass = 'border-b border-gray-200'; // 30-minute boundaries
          } else {
            borderBottomClass = 'border-b border-gray-100'; // 15-minute boundaries
          }

          // Apply red hue to non-working days
          const baseBackground = isWorking ? '' : 'bg-red-50/30';
          const hoverBackground = isWorking ? 'hover:bg-primary-50' : 'hover:bg-red-100/50';

          return (
            <div
              key={time}
              className={`grid grid-cols-[80px_1fr] transition-all duration-300 slide-up-element ${hoverBackground}`}
              style={{ animationDelay: `${timeIndex * 0.02}s` }}
            >
              {/* Time label column - show hour labels in the middle of each hour block */}
              <div className="p-2 text-right text-sm text-neutral-500 bg-neutral-50 border-r border-gray-300 relative">
                {timeIndex % 4 === 1 ? ( // Show in the second slot (middle of the hour)
                  <span className="absolute right-2 top-1/2 transform -translate-y-1/2">
                    {time.split(':')[0]}:00
                  </span>
                ) : null}
              </div>

              {/* Time slot column with drag and drop */}
              <div
                className={`p-1 min-h-[15px] relative group cursor-pointer ${borderBottomClass} ${baseBackground} ${isSelected ? 'bg-blue-200 border-blue-400' : ''}`}
                style={{
                  backgroundColor: isSelected ? 'rgba(59, 130, 246, 0.3)' : '',
                  borderColor: isSelected ? '#3b82f6' : ''
                }}
                onClick={() => onTimeSlotClick(dayStr, time)}
                onMouseDown={(e) => handleMouseDown(e, dayStr, time)}
                onMouseEnter={() => handleMouseEnter(dayStr, time)}
                onDragOver={(e) => handleTimeSlotDragOver(e, dayStr, time)}
                onDrop={(e) => handleTimeSlotDrop(e, dayStr, time)}
                onDragLeave={handleTimeSlotDragLeave}
              >
                {/* Appointments in this time slot */}
                {dayAppointments.map((appointment, aptIndex) => (
                  <div
                    key={appointment.id}
                    className="absolute inset-x-1 rounded-lg p-1 text-xs shadow-medium hover:opacity-80 transition-opacity cursor-pointer z-10"
                    style={{
                      top: `${aptIndex * 2}px`,
                      height: `${getAppointmentDuration(appointment)}px`,
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      backgroundColor: getAppointmentBackgroundColor(appointment.status),
                      color: getAppointmentTextColor(appointment.status)
                    }}
                    onClick={(e) => {
                      e.stopPropagation();
                      onAppointmentClick(appointment);
                    }}
                    onMouseEnter={(e) => {
                      e.stopPropagation();
                    }}
                    onMouseDown={(e) => {
                      e.stopPropagation();
                    }}
                    draggable
                    onDragStart={(e) => handleAppointmentDragStart(e, appointment)}
                    onDragEnd={handleAppointmentDragEnd}
                  >
                    <p className="font-semibold truncate">{getPatientName(appointment)}</p>
                    <p className="opacity-90 truncate">{appointment.type}</p>
                  </div>
                ))}

                {/* Drag preview shadow - shows where appointment will be dropped */}
                {draggedAppointment && dragPreviewPosition &&
                 dragPreviewPosition.date === dayStr && dragPreviewPosition.time === time && (
                  <div
                    className="absolute inset-x-1 rounded-lg p-1 text-xs shadow-lg border-2 border-dashed border-primary-400 bg-primary-100/50 pointer-events-none z-20"
                    style={{
                      top: '1px',
                      height: `${getAppointmentDuration(draggedAppointment)}px`,
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      backdropFilter: 'blur(1px)',
                    }}
                  >
                    <div className="text-primary-700 font-medium opacity-75">
                      {getPatientName(draggedAppointment)}
                    </div>
                    <div className="text-primary-600 text-xs opacity-60">
                      Moving...
                    </div>
                  </div>
                )}

                {/* Hover effect */}
                <div className={`absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300 rounded-lg ${
                  isWorking ? 'bg-primary-100/30' : 'bg-red-100/30'
                }`}></div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

import React from 'react';
import { Appointment, AppointmentStatus } from '../../types/Appointment';

interface DayViewProps {
  currentDate: Date;
  appointments: Appointment[];
  onTimeSlotClick: (date: string, time: string) => void;
  onAppointmentClick: (appointment: Appointment) => void;
  onAppointmentDrop?: (appointment: Appointment, newDate: string, newTime: string) => void;
  getAppointmentDate: (apt: Appointment) => string;
  formatAppointmentTime: (apt: Appointment) => string;
  getAppointmentDuration: (apt: Appointment) => number;
  getAppointmentBackgroundColor: (status: AppointmentStatus) => string;
  getAppointmentTextColor: (status: AppointmentStatus) => string;
  getPatientName: (apt: Appointment) => string;
  // Add drag and drop props similar to WeeklyView
  handleMouseUp: () => void;
  handleMouseDown: (e: React.MouseEvent, date: string, time: string) => void;
  handleMouseEnter: (date: string, time: string) => void;
  isSlotSelected: (date: string, time: string) => boolean;
  workingHours?: {
    startTime: string;
    endTime: string;
  };
  workingDays?: string[];
}

export const DayView: React.FC<DayViewProps> = ({
  currentDate,
  appointments,
  onTimeSlotClick,
  onAppointmentClick,
  onAppointmentDrop,
  getAppointmentDate,
  formatAppointmentTime,
  getAppointmentDuration,
  getAppointmentBackgroundColor,
  getAppointmentTextColor,
  getPatientName,
  handleMouseUp,
  handleMouseDown,
  handleMouseEnter,
  isSlotSelected,
  workingHours = { startTime: '08:00', endTime: '17:00' },
  workingDays = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'],
}) => {
  const [draggedAppointment, setDraggedAppointment] = React.useState<Appointment | null>(null);
  const [dragPreviewPosition, setDragPreviewPosition] = React.useState<{date: string, time: string} | null>(null);

  // Check if current date is a working day
  const isWorkingDay = (): boolean => {
    const dayName = currentDate.toLocaleDateString('en-US', { weekday: 'long' });
    return workingDays.includes(dayName);
  };

  const isWorking = isWorkingDay();

  // Generate time slots based on working hours
  const generateTimeSlots = () => {
    const startHour = parseInt(workingHours.startTime.split(':')[0]);
    const endHour = parseInt(workingHours.endTime.split(':')[0]);
    const totalHours = endHour - startHour;
    const slotsPerHour = 4; // 15-minute intervals
    const totalSlots = totalHours * slotsPerHour;

    return Array.from({ length: totalSlots }, (_, i) => {
      const hour = Math.floor(i / slotsPerHour) + startHour;
      const minutes = (i % slotsPerHour) * 15;
      return `${hour.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
    });
  };

  const timeSlots = generateTimeSlots();

  // Handle appointment drag start
  const handleAppointmentDragStart = (e: React.DragEvent, appointment: Appointment) => {
    e.stopPropagation();
    setDraggedAppointment(appointment);
    e.dataTransfer.setData('text/plain', appointment.id);
    e.dataTransfer.effectAllowed = 'move';
  };

  // Handle appointment drag end
  const handleAppointmentDragEnd = () => {
    setDraggedAppointment(null);
    setDragPreviewPosition(null);
  };

  // Handle drop on time slot
  const handleTimeSlotDrop = (e: React.DragEvent, date: string, time: string) => {
    e.preventDefault();
    e.stopPropagation();

    if (draggedAppointment && onAppointmentDrop) {
      onAppointmentDrop(draggedAppointment, date, time);
    }
    setDraggedAppointment(null);
    setDragPreviewPosition(null);
  };

  // Handle drag over to allow drop and show preview
  const handleTimeSlotDragOver = (e: React.DragEvent, date: string, time: string) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';

    if (draggedAppointment) {
      setDragPreviewPosition({ date, time });
    }
  };

  // Handle drag leave to hide preview when leaving time slot
  const handleTimeSlotDragLeave = (e: React.DragEvent) => {
    // Only hide preview if we're actually leaving the time slot area
    const rect = (e.currentTarget as HTMLElement).getBoundingClientRect();
    const { clientX, clientY } = e;

    if (clientX < rect.left || clientX > rect.right || clientY < rect.top || clientY > rect.bottom) {
      setDragPreviewPosition(null);
    }
  };

  const dayStr = currentDate.toISOString().split('T')[0];

  return (
    <div className="card overflow-hidden">
      {/* Day header - similar to week header but for a single day */}
      <div className="grid grid-cols-[80px_1fr] border-b border-gray-300">
        <div className="p-2"></div>
        <div
          className={`p-4 text-center slide-up-element ${
            currentDate.toDateString() === new Date().toDateString()
              ? isWorking
                ? 'bg-primary-50 border-primary-200'
                : 'bg-red-200'
              : isWorking
              ? 'bg-neutral-50'
              : 'bg-red-50'
          }`}
          style={{ animationDelay: '0.05s' }}
        >
          <p className={`text-sm ${
            currentDate.toDateString() === new Date().toDateString()
              ? isWorking 
                ? 'text-neutral-500'
                : 'text-red-600'
              : isWorking 
              ? 'text-neutral-500' 
              : 'text-red-400'
          }`}>
            {currentDate.toLocaleDateString('en-US', { weekday: 'short' })}
          </p>
          <p className={`text-lg font-semibold ${
            currentDate.toDateString() === new Date().toDateString()
              ? isWorking
                ? 'text-primary-600'
                : 'text-red-700'
              : isWorking
              ? 'text-neutral-800'
              : 'text-red-600'
          }`}>
            {currentDate.getDate()}
          </p>
        </div>
      </div>

      {/* Time slots grid - 15 minute precision with drag and drop */}
      <div
        className="flex-1 overflow-y-auto"
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        style={{ height: 'calc(100vh - 280px)' }}
      >
        {timeSlots.map((time, timeIndex) => {
          const isSelected = isSlotSelected(dayStr, time);
          const dayAppointments = appointments.filter(apt => {
            const aptDate = getAppointmentDate(apt);
            const aptTime = formatAppointmentTime(apt);
            const aptHour = parseInt(aptTime.split(':')[0]);
            const aptMinute = parseInt(aptTime.split(':')[1]);
            const timeHour = parseInt(time.split(':')[0]);
            const timeMinute = parseInt(time.split(':')[1]);

            // Only show appointment in its starting time slot
            return aptDate === dayStr &&
              aptHour === timeHour &&
              aptMinute === timeMinute;
          });

          // Define border classes for visual hierarchy
          let borderBottomClass;
          if (timeIndex % 4 === 3) {
            borderBottomClass = 'border-b border-gray-300'; // Hour boundaries
          } else if (timeIndex % 2 === 1) {
            borderBottomClass = 'border-b border-gray-200'; // 30-minute boundaries
          } else {
            borderBottomClass = 'border-b border-gray-100'; // 15-minute boundaries
          }

          // Apply red hue to non-working days
          const baseBackground = isWorking ? '' : 'bg-red-50/30';
          const hoverBackground = isWorking ? 'hover:bg-primary-50' : 'hover:bg-red-100/50';

          return (
            <div
              key={time}
              className={`grid grid-cols-[80px_1fr] transition-all duration-300 slide-up-element ${hoverBackground}`}
              style={{ animationDelay: `${timeIndex * 0.02}s` }}
            >
              {/* Time label column - show hour labels in the middle of each hour block */}
              <div className="p-2 text-right text-sm text-neutral-500 bg-neutral-50 border-r border-gray-300 relative">
                {timeIndex % 4 === 1 ? ( // Show in the second slot (middle of the hour)
                  <span className="absolute right-2 top-1/2 transform -translate-y-1/2">
                    {time.split(':')[0]}:00
                  </span>
                ) : null}
              </div>

              {/* Time slot column with drag and drop */}
              <div
                className={`p-1 min-h-[15px] relative group cursor-pointer ${borderBottomClass} ${baseBackground} ${isSelected ? 'bg-blue-200 border-blue-400' : ''}`}
                style={{
                  backgroundColor: isSelected ? 'rgba(59, 130, 246, 0.3)' : '',
                  borderColor: isSelected ? '#3b82f6' : ''
                }}
                onClick={() => onTimeSlotClick(dayStr, time)}
                onMouseDown={(e) => handleMouseDown(e, dayStr, time)}
                onMouseEnter={() => handleMouseEnter(dayStr, time)}
                onDragOver={(e) => handleTimeSlotDragOver(e, dayStr, time)}
                onDrop={(e) => handleTimeSlotDrop(e, dayStr, time)}
                onDragLeave={handleTimeSlotDragLeave}
              >
                {/* Appointments in this time slot */}
                {dayAppointments.map((appointment, aptIndex) => (
                  <div
                    key={appointment.id}
                    className="absolute inset-x-1 rounded-lg p-1 text-xs shadow-medium hover:opacity-80 transition-opacity cursor-pointer z-10"
                    style={{
                      top: `${aptIndex * 2}px`,
                      height: `${getAppointmentDuration(appointment)}px`,
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      backgroundColor: getAppointmentBackgroundColor(appointment.status),
                      color: getAppointmentTextColor(appointment.status)
                    }}
                    onClick={(e) => {
                      e.stopPropagation();
                      onAppointmentClick(appointment);
                    }}
                    onMouseEnter={(e) => {
                      e.stopPropagation();
                    }}
                    onMouseDown={(e) => {
                      e.stopPropagation();
                    }}
                    draggable
                    onDragStart={(e) => handleAppointmentDragStart(e, appointment)}
                    onDragEnd={handleAppointmentDragEnd}
                  >
                    <p className="font-semibold truncate">{getPatientName(appointment)}</p>
                    <p className="opacity-90 truncate">{appointment.type}</p>
                  </div>
                ))}

                {/* Drag preview shadow - shows where appointment will be dropped */}
                {draggedAppointment && dragPreviewPosition &&
                 dragPreviewPosition.date === dayStr && dragPreviewPosition.time === time && (
                  <div
                    className="absolute inset-x-1 rounded-lg p-1 text-xs shadow-lg border-2 border-dashed border-primary-400 bg-primary-100/50 pointer-events-none z-20"
                    style={{
                      top: '1px',
                      height: `${getAppointmentDuration(draggedAppointment)}px`,
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      backdropFilter: 'blur(1px)',
                    }}
                  >
                    <div className="text-primary-700 font-medium opacity-75">
                      {getPatientName(draggedAppointment)}
                    </div>
                    <div className="text-primary-600 text-xs opacity-60">
                      Moving...
                    </div>
                  </div>
                )}

                {/* Hover effect */}
                <div className={`absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300 rounded-lg ${
                  isWorking ? 'bg-primary-100/30' : 'bg-red-100/30'
                }`}></div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

interface AppointmentData {
  id: string;
  patientName: string;
  time: string;
  duration: number;
  type: string;
  status: string;
  title: string;
}

interface MonthViewProps {
  currentDate: Date;
  appointments: Record<string, AppointmentData[]>;
  onAppointmentClick?: (appointmentId: string) => void;
  onDayClick?: (date: Date) => void;
}

export const MonthView = ({ currentDate, appointments, onAppointmentClick, onDayClick }: MonthViewProps) => {
  const getMonthDays = (date: Date): Date[] => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);

    // Get the first day to display (might be from previous month)
    const startDate = new Date(firstDay);
    startDate.setDate(firstDay.getDate() - firstDay.getDay());

    const days: Date[] = [];
    const currentDay = new Date(startDate);

    // Generate 6 weeks of dates
    for (let i = 0; i < 42; i++) {
      days.push(new Date(currentDay));
      currentDay.setDate(currentDay.getDate() + 1);
    }

    return days;
  };

  const isToday = (date: Date): boolean => {
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  const isCurrentMonth = (date: Date): boolean => {
    return date.getMonth() === currentDate.getMonth();
  };

  const getAppointmentsForDate = (date: string): AppointmentData[] => {
    return appointments[date] || [];
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'Booked':
        return {
          bg: 'bg-blue-100',
          text: 'text-blue-800'
        };
      case 'Completed':
        return {
          bg: 'bg-green-100',
          text: 'text-green-800'
        };
      case 'Cancelled':
        return {
          bg: 'bg-red-100',
          text: 'text-red-800'
        };
      case 'No Show':
        return {
          bg: 'bg-orange-100',
          text: 'text-orange-800'
        };
      default:
        return {
          bg: 'bg-gray-100',
          text: 'text-gray-800'
        };
    }
  };

  const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const monthDays = getMonthDays(currentDate);

  return (
    <div className="w-full h-full flex flex-col">
      <div className="grid grid-cols-7 gap-px bg-gray-200 flex-shrink-0">
        {weekDays.map((day, index) => (
          <div
            key={day}
            className="bg-white p-2 text-sm font-medium text-gray-600 text-center slide-up-element"
            style={{ animationDelay: `${index * 0.05}s` }}
          >
            {day}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-px bg-gray-200 flex-1" style={{ height: 'calc(100vh - 320px)' }}>
        {monthDays.map((date, index) => {
          const dateString = date.toISOString().split('T')[0];
          const dayAppointments = getAppointmentsForDate(dateString);

          return (
            <div
              key={index}
              className={`bg-white min-h-[120px] p-2 border-t relative cursor-pointer hover:bg-gray-50 transition-colors slide-up-element ${
                isCurrentMonth(date) ? 'bg-white' : 'bg-gray-50'
              }`}
              style={{ animationDelay: `${index * 0.02}s` }}
              onClick={() => onDayClick && onDayClick(date)}
            >
              <div className={`text-sm ${
                isToday(date)
                  ? 'bg-blue-600 text-white w-6 h-6 rounded-full flex items-center justify-center'
                  : isCurrentMonth(date)
                    ? 'text-gray-900'
                    : 'text-gray-400'
              }`}>
                {date.getDate()}
              </div>

              <div className="mt-1 space-y-1">
                {dayAppointments.slice(0, 3).map((apt, aptIndex) => {
                  const statusColors = getStatusColor(apt.status);
                  return (
                    <div
                      key={apt.id}
                      className={`text-xs ${statusColors.bg} ${statusColors.text} p-1 rounded truncate cursor-pointer hover:opacity-80 transition-opacity slide-up-element`}
                      style={{ animationDelay: `${(index * 0.02) + (aptIndex * 0.01)}s` }}
                      onClick={(e) => {
                        e.stopPropagation(); // Prevent day click when clicking appointment
                        onAppointmentClick && onAppointmentClick(apt.id);
                      }}
                    >
                      <div className="font-medium">{apt.time}</div>
                      <div className="truncate">{apt.patientName}</div>
                    </div>
                  );
                })}
                {dayAppointments.length > 3 && (
                  <div
                    className="text-xs text-gray-500 pl-1 slide-up-element"
                    style={{ animationDelay: `${(index * 0.02) + 0.03}s` }}
                  >
                    +{dayAppointments.length - 3} more
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

interface AppointmentData {
  id: string;
  patientName: string;
  time: string;
  duration: number;
  type: string;
  status: string;
  title: string;
}

interface MonthViewProps {
  currentDate: Date;
  appointments: Record<string, AppointmentData[]>;
  onAppointmentClick?: (appointmentId: string) => void;
  onDayClick?: (date: Date) => void;
}

export const MonthView = ({ currentDate, appointments, onAppointmentClick, onDayClick }: MonthViewProps) => {
  const getMonthDays = (date: Date): Date[] => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);

    // Get the first day to display (might be from previous month)
    const startDate = new Date(firstDay);
    startDate.setDate(firstDay.getDate() - firstDay.getDay());

    const days: Date[] = [];
    const currentDay = new Date(startDate);

    // Generate 6 weeks of dates
    for (let i = 0; i < 42; i++) {
      days.push(new Date(currentDay));
      currentDay.setDate(currentDay.getDate() + 1);
    }

    return days;
  };

  const isToday = (date: Date): boolean => {
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  const isCurrentMonth = (date: Date): boolean => {
    return date.getMonth() === currentDate.getMonth();
  };

  const getAppointmentsForDate = (date: string): AppointmentData[] => {
    return appointments[date] || [];
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'Booked':
        return {
          bg: 'bg-blue-100',
          text: 'text-blue-800'
        };
      case 'Completed':
        return {
          bg: 'bg-green-100',
          text: 'text-green-800'
        };
      case 'Cancelled':
        return {
          bg: 'bg-red-100',
          text: 'text-red-800'
        };
      case 'No Show':
        return {
          bg: 'bg-orange-100',
          text: 'text-orange-800'
        };
      default:
        return {
          bg: 'bg-gray-100',
          text: 'text-gray-800'
        };
    }
  };

  const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const monthDays = getMonthDays(currentDate);

  return (
    <div className="w-full h-full flex flex-col">
      <div className="grid grid-cols-7 gap-px bg-gray-200 flex-shrink-0">
        {weekDays.map((day, index) => (
          <div
            key={day}
            className="bg-white p-2 text-sm font-medium text-gray-600 text-center slide-up-element"
            style={{ animationDelay: `${index * 0.02}s` }}
          >
            {day}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-px bg-gray-200 flex-1" style={{ height: 'calc(100vh - 320px)' }}>
        {monthDays.map((date, index) => {
          const dateString = date.toISOString().split('T')[0];
          const dayAppointments = getAppointmentsForDate(dateString);

          return (
            <div
              key={index}
              className={`bg-white min-h-[120px] p-2 border-t relative cursor-pointer hover:bg-gray-50 transition-colors slide-up-element ${
                isCurrentMonth(date) ? 'bg-white' : 'bg-gray-50'
              }`}
              style={{ animationDelay: `${index * 0.01}s` }}
              onClick={() => onDayClick && onDayClick(date)}
            >
              <div className={`text-sm ${
                isToday(date)
                  ? 'bg-blue-600 text-white w-6 h-6 rounded-full flex items-center justify-center'
                  : isCurrentMonth(date)
                    ? 'text-gray-900'
                    : 'text-gray-400'
              }`}>
                {date.getDate()}
              </div>

              <div className="mt-1 space-y-1">
                {dayAppointments.slice(0, 3).map((apt, aptIndex) => {
                  const statusColors = getStatusColor(apt.status);
                  return (
                    <div
                      key={apt.id}
                      className={`text-xs ${statusColors.bg} ${statusColors.text} p-1 rounded truncate cursor-pointer hover:opacity-80 transition-opacity slide-up-element`}
                      style={{ animationDelay: `${(index * 0.01) + (aptIndex * 0.005)}s` }}
                      onClick={(e) => {
                        e.stopPropagation(); // Prevent day click when clicking appointment
                        onAppointmentClick && onAppointmentClick(apt.id);
                      }}
                    >
                      <div className="font-medium">{apt.time}</div>
                      <div className="truncate">{apt.patientName}</div>
                    </div>
                  );
                })}
                {dayAppointments.length > 3 && (
                  <div 
                    className="text-xs text-gray-500 pl-1 slide-up-element"
                    style={{ animationDelay: `${(index * 0.01) + 0.015}s` }}
                  >
                    +{dayAppointments.length - 3} more
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

import React from 'react';
import { Appointment, AppointmentStatus } from '../../types/Appointment';

interface WeeklyViewProps {
  isSlotSelected: (date: string, time: string) => boolean;
  currentDate: Date;
  appointments: Appointment[];
  handleMouseUp: () => void;
  handleMouseDown: (e: React.MouseEvent, date: string, time: string) => void;
  handleMouseEnter: (date: string, time: string) => void;
  onSelectSlot: (date: string, time: string) => void;
  onAppointmentClick: (appointment: Appointment) => void;
  onAppointmentDrop?: (appointment: Appointment, newDate: string, newTime: string) => void;
  onDayClick?: (date: Date) => void;
  getAppointmentDate: (apt: Appointment) => string;
  formatAppointmentTime: (apt: Appointment) => string;
  getAppointmentDuration: (apt: Appointment) => number;
  getAppointmentBackgroundColor: (status: AppointmentStatus) => string;
  getAppointmentTextColor: (status: AppointmentStatus) => string;
  workingHours?: {
    startTime: string;
    endTime: string;
  };
  workingDays?: string[];
}

export const WeeklyView: React.FC<WeeklyViewProps> = ({
  currentDate,
  appointments,
  handleMouseUp,
  handleMouseDown,
  handleMouseEnter,
  onSelectSlot,
  onAppointmentClick,
  isSlotSelected,
  getAppointmentDate,
  formatAppointmentTime,
  getAppointmentDuration,
  getAppointmentBackgroundColor,
  getAppointmentTextColor,
  onAppointmentDrop,
  onDayClick,
  workingHours = { startTime: '08:00', endTime: '17:00' },
  workingDays = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'],
}) => {
  const [draggedAppointment, setDraggedAppointment] = React.useState<Appointment | null>(null);
  const [dragPreviewPosition, setDragPreviewPosition] = React.useState<{date: string, time: string} | null>(null);

  // Handle appointment drag start
  const handleAppointmentDragStart = (e: React.DragEvent, appointment: Appointment) => {
    e.stopPropagation();
    setDraggedAppointment(appointment);
    e.dataTransfer.setData('text/plain', appointment.id);
    e.dataTransfer.effectAllowed = 'move';
  };

  // Handle appointment drag end
  const handleAppointmentDragEnd = () => {
    setDraggedAppointment(null);
    setDragPreviewPosition(null);
  };

  // Handle drop on time slot
  const handleTimeSlotDrop = (e: React.DragEvent, date: string, time: string) => {
    e.preventDefault();
    e.stopPropagation();

    if (draggedAppointment && onAppointmentDrop) {
      onAppointmentDrop(draggedAppointment, date, time);
    }
    setDraggedAppointment(null);
    setDragPreviewPosition(null);
  };

  // Handle drag over to allow drop and show preview
  const handleTimeSlotDragOver = (e: React.DragEvent, date: string, time: string) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';

    if (draggedAppointment) {
      setDragPreviewPosition({ date, time });
    }
  };

  // Handle drag leave to hide preview when leaving time slot
  const handleTimeSlotDragLeave = (e: React.DragEvent) => {
    // Only hide preview if we're actually leaving the time slot area
    const rect = (e.currentTarget as HTMLElement).getBoundingClientRect();
    const { clientX, clientY } = e;

    if (clientX < rect.left || clientX > rect.right || clientY < rect.top || clientY > rect.bottom) {
      setDragPreviewPosition(null);
    }
  };

  const weekStart = new Date(currentDate);
  weekStart.setDate(currentDate.getDate() - currentDate.getDay());

  const days = Array.from({ length: 7 }, (_, i) => {
    const day = new Date(weekStart);
    day.setDate(weekStart.getDate() + i);
    return day;
  });

  // Check if a day is a working day
  const isWorkingDay = (date: Date): boolean => {
    const dayName = date.toLocaleDateString('en-US', { weekday: 'long' });
    return workingDays.includes(dayName);
  };

  // Generate time slots based on working hours
  const generateTimeSlots = () => {
    const startHour = parseInt(workingHours.startTime.split(':')[0]);
    const endHour = parseInt(workingHours.endTime.split(':')[0]);
    const totalHours = endHour - startHour;
    const slotsPerHour = 4; // 15-minute intervals
    const totalSlots = totalHours * slotsPerHour;

    return Array.from({ length: totalSlots }, (_, i) => {
      const hour = Math.floor(i / slotsPerHour) + startHour;
      const minutes = (i % slotsPerHour) * 15;
      return `${hour.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
    });
  };

  const timeSlots = generateTimeSlots();

  return (
    <div className="card overflow-hidden">
      {/* Week header */}
      <div className="grid grid-cols-8 border-b border-gray-300">
        <div className="p-4"></div>
        {days.map((day, index) => {
          const isWorking = isWorkingDay(day);
          const isToday = day.toDateString() === new Date().toDateString();

          return (
            <div
              key={index}
              className={`p-4 text-center slide-up-element cursor-pointer transition-colors duration-200 ${
                isToday
                  ? 'bg-primary-50 border-primary-200'
                  : 'bg-red-50 hover:bg-red-100'
              }`}
              style={{ animationDelay: `${index * 0.05}s` }}
              onClick={() => onDayClick && onDayClick(day)}
            >
              <p className={`text-sm ${
                isWorking ? 'text-neutral-500' : 'text-red-400'
              }`}>
                isWorking ? 'text-neutral-500' : 'text-red-400'
                  : 'text-red-600'
              }`}>
                {day.getDate()}
              </p>
              {!isWorking && (
                  ? 'text-primary-600'
          );
        })}
      </div>

      {/* Time slots grid - 15 minute precision */}
      <div
              {!isWorking && (
                <p className="text-xs text-red-500 mt-1">Closed</p>
              )}
        className="flex-1 overflow-y-auto"
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        style={{ height: 'calc(100vh - 280px)' }}
      >
        {timeSlots.map((time, timeIndex) => (
          <div key={time} className="grid grid-cols-8 hover:bg-primary-50 transition-all duration-300">
            <div className="p-2 text-right text-sm text-neutral-500 bg-neutral-50 border-r border-gray-300 relative">
              {timeIndex % 4 === 1 ? ( // Show in the second slot (middle of the hour)
                <span className="absolute right-2 top-1/2 transform -translate-y-1/2">
                  {time.split(':')[0]}:00
                </span>
              ) : null}
            </div>
            {days.map((day) => {
              const dayStr = day.toISOString().split('T')[0];
              const isSelected = isSlotSelected(dayStr, time);
              const isWorking = isWorkingDay(day);
              const dayAppointments = appointments.filter(apt => {
                const aptDate = getAppointmentDate(apt);
                const aptTime = formatAppointmentTime(apt);
                const aptHour = parseInt(aptTime.split(':')[0]);
                const aptMinute = parseInt(aptTime.split(':')[1]);
                const timeHour = parseInt(time.split(':')[0]);
                const timeMinute = parseInt(time.split(':')[1]);

                // Only show appointment in its starting time slot
                return aptDate === dayStr &&
                  aptHour === timeHour &&
                  aptMinute === timeMinute;
              });

              let borderBottomClass;
              if (timeIndex % 4 === 3) {
                borderBottomClass = 'border-b border-gray-300';
              } else if (timeIndex % 2 === 1) {
                borderBottomClass = 'border-b border-gray-200';
              } else {
                borderBottomClass = 'border-b border-gray-100';
              }

              // Apply red hue to non-working days
              const baseBackground = isWorking ? '' : 'bg-red-50/30';
              const hoverBackground = isWorking ? 'hover:bg-primary-50' : 'hover:bg-red-100/50';

              return (
                <div
                  key={`${dayStr}-${time}`}
                  className={`p-1 min-h-[15px] relative group cursor-pointer border-r border-gray-200 ${borderBottomClass} ${baseBackground} ${hoverBackground} ${isSelected ? 'bg-blue-200 border-blue-400' : ''}`}
                  style={{
                    backgroundColor: isSelected ? 'rgba(59, 130, 246, 0.3)' : '',
                    borderColor: isSelected ? '#3b82f6' : ''
                  }}
                  onClick={() => onSelectSlot(dayStr, time)}
                  onMouseDown={(e) => handleMouseDown(e, dayStr, time)}
                  onMouseEnter={() => handleMouseEnter(dayStr, time)}
                  onDragOver={(e) => handleTimeSlotDragOver(e, dayStr, time)}
                  onDrop={(e) => handleTimeSlotDrop(e, dayStr, time)}
                  onDragLeave={handleTimeSlotDragLeave}
                >
                  {dayAppointments.map((appointment, aptIndex) => (
                    <div
                      key={appointment.id}
                      className="absolute inset-x-1 rounded-lg p-1 text-xs shadow-medium hover:opacity-80 transition-opacity cursor-pointer z-10"
                      style={{
                        top: `${aptIndex * 2}px`,
                        height: `${getAppointmentDuration(appointment)}px`,
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        backgroundColor: getAppointmentBackgroundColor(appointment.status),
                        color: getAppointmentTextColor(appointment.status)
                      }}
                      onClick={(e) => {
                        e.stopPropagation();
                        onAppointmentClick(appointment);
                      }}
                      onMouseEnter={(e) => {
                        e.stopPropagation();
                      }}
                      onMouseDown={(e) => {
                        e.stopPropagation();
                      }}
                      draggable
                      onDragStart={(e) => handleAppointmentDragStart(e, appointment)}
                      onDragEnd={handleAppointmentDragEnd}
                    >
                      {appointment.patient_first_name} {appointment.patient_last_name}
                    </div>
                  ))}
                  {/* Drag preview shadow - shows where appointment will be dropped */}
                  {draggedAppointment && dragPreviewPosition &&
                   dragPreviewPosition.date === dayStr && dragPreviewPosition.time === time && (
                    <div
                      className="absolute inset-x-1 rounded-lg p-1 text-xs shadow-lg border-2 border-dashed border-primary-400 bg-primary-100/50 pointer-events-none z-20"
                      style={{
                        top: '1px',
                        height: `${getAppointmentDuration(draggedAppointment)}px`,
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        backdropFilter: 'blur(1px)',
                      }}
                    >
                      <div className="text-primary-700 font-medium opacity-75">
                        {draggedAppointment.patient_first_name} {draggedAppointment.patient_last_name}
                      </div>
                      <div className="text-primary-600 text-xs opacity-60">
                        Moving...
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
};

import React from 'react';
import { Appointment, AppointmentStatus } from '../../types/Appointment';

interface WeeklyViewProps {
  isSlotSelected: (date: string, time: string) => boolean;
  currentDate: Date;
  appointments: Appointment[];
  handleMouseUp: () => void;
  handleMouseDown: (e: React.MouseEvent, date: string, time: string) => void;
  handleMouseEnter: (date: string, time: string) => void;
  onSelectSlot: (date: string, time: string) => void;
  onAppointmentClick: (appointment: Appointment) => void;
  onAppointmentDrop?: (appointment: Appointment, newDate: string, newTime: string) => void;
  onDayClick?: (date: Date) => void;
  getAppointmentDate: (apt: Appointment) => string;
  formatAppointmentTime: (apt: Appointment) => string;
  getAppointmentDuration: (apt: Appointment) => number;
  getAppointmentBackgroundColor: (status: AppointmentStatus) => string;
  getAppointmentTextColor: (status: AppointmentStatus) => string;
  workingHours?: {
    startTime: string;
    endTime: string;
  };
  workingDays?: string[];
}

export const WeeklyView: React.FC<WeeklyViewProps> = ({
  currentDate,
  appointments,
  handleMouseUp,
  handleMouseDown,
  handleMouseEnter,
  onSelectSlot,
  onAppointmentClick,
  isSlotSelected,
  getAppointmentDate,
  formatAppointmentTime,
  getAppointmentDuration,
  getAppointmentBackgroundColor,
  getAppointmentTextColor,
  onAppointmentDrop,
  onDayClick,
  workingHours = { startTime: '08:00', endTime: '17:00' },
  workingDays = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'],
}) => {
  const [draggedAppointment, setDraggedAppointment] = React.useState<Appointment | null>(null);
  const [dragPreviewPosition, setDragPreviewPosition] = React.useState<{date: string, time: string} | null>(null);

  // Handle appointment drag start
  const handleAppointmentDragStart = (e: React.DragEvent, appointment: Appointment) => {
    e.stopPropagation();
    setDraggedAppointment(appointment);
    e.dataTransfer.setData('text/plain', appointment.id);
    e.dataTransfer.effectAllowed = 'move';
  };

  // Handle appointment drag end
  const handleAppointmentDragEnd = () => {
    setDraggedAppointment(null);
    setDragPreviewPosition(null);
  };

  // Handle drop on time slot
  const handleTimeSlotDrop = (e: React.DragEvent, date: string, time: string) => {
    e.preventDefault();
    e.stopPropagation();

    if (draggedAppointment && onAppointmentDrop) {
      onAppointmentDrop(draggedAppointment, date, time);
    }
    setDraggedAppointment(null);
    setDragPreviewPosition(null);
  };

  // Handle drag over to allow drop and show preview
  const handleTimeSlotDragOver = (e: React.DragEvent, date: string, time: string) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';

    if (draggedAppointment) {
      setDragPreviewPosition({ date, time });
    }
  };

  // Handle drag leave to hide preview when leaving time slot
  const handleTimeSlotDragLeave = (e: React.DragEvent) => {
    // Only hide preview if we're actually leaving the time slot area
    const rect = (e.currentTarget as HTMLElement).getBoundingClientRect();
    const { clientX, clientY } = e;

    if (clientX < rect.left || clientX > rect.right || clientY < rect.top || clientY > rect.bottom) {
      setDragPreviewPosition(null);
    }
  };

  const weekStart = new Date(currentDate);
  weekStart.setDate(currentDate.getDate() - currentDate.getDay());

  const days = Array.from({ length: 7 }, (_, i) => {
    const day = new Date(weekStart);
    day.setDate(weekStart.getDate() + i);
    return day;
  });

  // Check if a day is a working day
  const isWorkingDay = (date: Date): boolean => {
    const dayName = date.toLocaleDateString('en-US', { weekday: 'long' });
    return workingDays.includes(dayName);
  };

  // Generate time slots based on working hours
  const generateTimeSlots = () => {
    const startHour = parseInt(workingHours.startTime.split(':')[0]);
    const endHour = parseInt(workingHours.endTime.split(':')[0]);
    const totalHours = endHour - startHour;
    const slotsPerHour = 4; // 15-minute intervals
    const totalSlots = totalHours * slotsPerHour;

    return Array.from({ length: totalSlots }, (_, i) => {
      const hour = Math.floor(i / slotsPerHour) + startHour;
      const minutes = (i % slotsPerHour) * 15;
      return `${hour.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
    });
  };

  const timeSlots = generateTimeSlots();

  return (
    <div className="card overflow-hidden">
      {/* Week header */}
      <div className="grid grid-cols-8 border-b border-gray-300">
        <div className="p-4"></div>
        {days.map((day, index) => {
          const isWorking = isWorkingDay(day);
          const isToday = day.toDateString() === new Date().toDateString();

          return (
            <div
              key={index}
              className={`p-4 text-center slide-up-element cursor-pointer transition-colors duration-200 ${
                isToday
                  ? isWorking 
                    ? 'bg-primary-50 border-primary-200'
                    : 'bg-red-200 hover:bg-red-250'
                  : isWorking
                  ? 'bg-neutral-50 hover:bg-primary-50'
                  : 'bg-red-50 hover:bg-red-100'
              }`}
              style={{ animationDelay: `${index * 0.05}s` }}
              onClick={() => onDayClick && onDayClick(day)}
            >
              <p className={`text-sm ${
                isToday
                  ? isWorking 
                    ? 'text-neutral-500'
                    : 'text-red-600'
                  : isWorking 
                  ? 'text-neutral-500' 
                  : 'text-red-400'
              }`}>
                {day.toLocaleDateString('en-US', { weekday: 'short' })}
              </p>
              <p className={`text-lg font-semibold ${
                isToday
                  ? isWorking
                    ? 'text-primary-600'
                    : 'text-red-700'
                  : isWorking
                  ? 'text-neutral-800'
                  : 'text-red-600'
              }`}>
                {day.getDate()}
              </p>
            </div>
          );
        })}
      </div>

      {/* Time slots grid - 15 minute precision */}
      <div
        className="flex-1 overflow-y-auto"
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        style={{ height: 'calc(100vh - 280px)' }}
      >
        {timeSlots.map((time, timeIndex) => (
          <div key={time} className="grid grid-cols-8 hover:bg-primary-50 transition-all duration-300">
            <div className="p-2 text-right text-sm text-neutral-500 bg-neutral-50 border-r border-gray-300 relative">
              {timeIndex % 4 === 1 ? ( // Show in the second slot (middle of the hour)
                <span className="absolute right-2 top-1/2 transform -translate-y-1/2">
                  {time.split(':')[0]}:00
                </span>
              ) : null}
            </div>
            {days.map((day) => {
              const dayStr = day.toISOString().split('T')[0];
              const isSelected = isSlotSelected(dayStr, time);
              const isWorking = isWorkingDay(day);
              const dayAppointments = appointments.filter(apt => {
                const aptDate = getAppointmentDate(apt);
                const aptTime = formatAppointmentTime(apt);
                const aptHour = parseInt(aptTime.split(':')[0]);
                const aptMinute = parseInt(aptTime.split(':')[1]);
                const timeHour = parseInt(time.split(':')[0]);
                const timeMinute = parseInt(time.split(':')[1]);

                // Only show appointment in its starting time slot
                return aptDate === dayStr &&
                  aptHour === timeHour &&
                  aptMinute === timeMinute;
              });

              let borderBottomClass;
              if (timeIndex % 4 === 3) {
                borderBottomClass = 'border-b border-gray-300';
              } else if (timeIndex % 2 === 1) {
                borderBottomClass = 'border-b border-gray-200';
              } else {
                borderBottomClass = 'border-b border-gray-100';
              }

              // Apply red hue to non-working days
              const baseBackground = isWorking ? '' : 'bg-red-50/30';
              const hoverBackground = isWorking ? 'hover:bg-primary-50' : 'hover:bg-red-100/50';

              return (
                <div
                  key={`${dayStr}-${time}`}
                  className={`p-1 min-h-[15px] relative group cursor-pointer border-r border-gray-200 ${borderBottomClass} ${baseBackground} ${hoverBackground} ${isSelected ? 'bg-blue-200 border-blue-400' : ''}`}
                  style={{
                    backgroundColor: isSelected ? 'rgba(59, 130, 246, 0.3)' : '',
                    borderColor: isSelected ? '#3b82f6' : ''
                  }}
                  onClick={() => onSelectSlot(dayStr, time)}
                  onMouseDown={(e) => handleMouseDown(e, dayStr, time)}
                  onMouseEnter={() => handleMouseEnter(dayStr, time)}
                  onDragOver={(e) => handleTimeSlotDragOver(e, dayStr, time)}
                  onDrop={(e) => handleTimeSlotDrop(e, dayStr, time)}
                  onDragLeave={handleTimeSlotDragLeave}
                >
                  {dayAppointments.map((appointment, aptIndex) => (
                    <div
                      key={appointment.id}
                      className="absolute inset-x-1 rounded-lg p-1 text-xs shadow-medium hover:opacity-80 transition-opacity cursor-pointer z-10"
                      style={{
                        top: `${aptIndex * 2}px`,
                        height: `${getAppointmentDuration(appointment)}px`,
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        backgroundColor: getAppointmentBackgroundColor(appointment.status),
                        color: getAppointmentTextColor(appointment.status)
                      }}
                      onClick={(e) => {
                        e.stopPropagation();
                        onAppointmentClick(appointment);
                      }}
                      onMouseEnter={(e) => {
                        e.stopPropagation();
                      }}
                      onMouseDown={(e) => {
                        e.stopPropagation();
                      }}
                      draggable
                      onDragStart={(e) => handleAppointmentDragStart(e, appointment)}
                      onDragEnd={handleAppointmentDragEnd}
                    >
                      {appointment.patient_first_name} {appointment.patient_last_name}
                    </div>
                  ))}
                  {/* Drag preview shadow - shows where appointment will be dropped */}
                  {draggedAppointment && dragPreviewPosition &&
                   dragPreviewPosition.date === dayStr && dragPreviewPosition.time === time && (
                    <div
                      className="absolute inset-x-1 rounded-lg p-1 text-xs shadow-lg border-2 border-dashed border-primary-400 bg-primary-100/50 pointer-events-none z-20"
                      style={{
                        top: '1px',
                        height: `${getAppointmentDuration(draggedAppointment)}px`,
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        backdropFilter: 'blur(1px)',
                      }}
                    >
                      <div className="text-primary-700 font-medium opacity-75">
                        {draggedAppointment.patient_first_name} {draggedAppointment.patient_last_name}
                      </div>
                      <div className="text-primary-600 text-xs opacity-60">
                        Moving...
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
};

import { API_CONFIG, buildApiUrl } from '../config/api';
import { LoginRequest, LoginResponse } from '../types/Auth';

class AuthService {
  private static instance: AuthService;

  public static getInstance(): AuthService {
    if (!AuthService.instance) {
      AuthService.instance = new AuthService();
    }
    return AuthService.instance;
  }

  async login(credentials: LoginRequest): Promise<LoginResponse> {
    try {
      const response = await fetch(buildApiUrl('/api/v1/auth/login'), {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });

      if (!response.ok) {
        // Try to parse error message from response
        let errorMessage = 'Login failed';
        try {
          const errorData = await response.json();
          errorMessage = errorData.message || errorData.detail || 'Invalid credentials';
        } catch {
          // Fallback to status-based messages if JSON parsing fails
          switch (response.status) {
            case 401:
              errorMessage = 'Invalid email or password';
              break;
            case 403:
              errorMessage = 'Access denied. Please contact administrator';
              break;
            case 429:
              errorMessage = 'Too many login attempts. Please try again later';
              break;
            case 500:
              errorMessage = 'Server error. Please try again later';
              break;
            default:
              errorMessage = 'Login failed. Please try again';
          }
        }
        throw new Error(errorMessage);
      }

      const data: LoginResponse = await response.json();

      // Store user data in localStorage
      localStorage.setItem('user', JSON.stringify(data));
      localStorage.setItem('isAuthenticated', 'true');

      return data;
    } catch (error) {
      // If it's already our custom error, re-throw it
      if (error instanceof Error) {
        throw error;
      }
      // Handle network errors or other unexpected errors
      throw new Error('Network error. Please check your connection and try again');
    }
  }

  logout(): void {
    localStorage.removeItem('user');
    localStorage.removeItem('isAuthenticated');
  }

  getCurrentUser(): LoginResponse | null {
    const userStr = localStorage.getItem('user');
    return userStr ? JSON.parse(userStr) : null;
  }

  isAuthenticated(): boolean {
    return localStorage.getItem('isAuthenticated') === 'true';
  }

  getDoctorId(): string | null {
    const user = this.getCurrentUser();
    console.log('Current user data:', user); // Debug log
    console.log('User data type:', typeof user); // Debug log
    console.log('User keys:', user ? Object.keys(user) : 'null'); // Debug log

    const doctorId = user?.doctor_id || null;
    return doctorId;
  }
}

export const authService = AuthService.getInstance();

